function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
		dsPilotItemCd1.ClearData();
		dsPilotRoundBas2.ClearData();
		dsPilotCcitTimeBas3.ClearData();
		dsPilotStTimeBas4.ClearData();

		dsPilotEstmItem5.ClearData();		//평가표
		dsPilotEstmDetl6.ClearData();		//평가표세부
		dsPilotEstmTimeDetl7.ClearData();	//주회타임
		dsPilotEstmTimeRec1.ClearData();	//기록
		dsPilotEstmTimeRec2.ClearData();	//기록
		dsPilotEstmTimeDetl8.ClearData();	//스타트타임

    }
}

