/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크

	var iDataCheck = 0;
	if(tabMain.TabIndex == 0){
		if(fnc_DatasetChangeCheck(dsPilotItemCd1) > 0){ //평가항목 
			iDataCheck++;

			// 필수항목체크
			oDataSet			= dsPilotItemCd1;
			sMandatoryColID   	= null;
			sMandatoryColName 	= null;
			oGrid             	= null;

			sMandatoryColID   = "ESTM_ITEM_CD|ESTM_ITEM_DETL|ASSIGN_SCR_LIMIT|ROUND";
			sMandatoryColName = "구분|세부평가항목|배점|차수";
			oGrid             = tabMain.tab1.grdPilotItemCd1;
	    	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
				return false;
			}
		}
		if(fnc_DatasetChangeCheck(dsPilotRoundBas2) > 0){ //차수별기준
			iDataCheck++;
		}		
		if(fnc_DatasetChangeCheck(dsPilotCcitTimeBas3) > 0){ //주회타임
			iDataCheck++;
		}
		if(fnc_DatasetChangeCheck(dsPilotStTimeBas4) > 0){ //스타트타임
			iDataCheck++;
		}
	}else{
		if(length(cbSearchRound.Value) <= 0){ //차수 체크
			//차수는 필수 저장
			fnc_Message(SNIS_CAN_A013, "N");
			return;
		}
		/*
		if(length(tabMain.tab2.cbDcsn.Value) <= 0){	 //합격구분 체크
			//합격구분은 필수 저장
			fnc_Message(SNIS_CAN_A014, "N");
			return;
		}
		*/

		if(length(tabMain.tab2.CalInputDt.Value) <= 0){	 //일자 체크
			//일자는 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}



		if(fnc_DatasetChangeCheck(dsPilotEstmDetl6) > 0){ //평가표 세부항목 
			iDataCheck++;
		}
		if(fnc_DatasetChangeCheck(dsPilotEstmTimeDetlScr1) > 0){ //평가표 세부 주회타임
			iDataCheck++;
		}		
		if(fnc_DatasetChangeCheck(dsPilotEstmTimeDetlScr2) > 0){ //평가표 세부 스타트타임
			iDataCheck++;
		}
		if(length(tabMain.tab2.Div0.edTimeDetl7Tot.Value) > 0){	 //주회타임 합계
			iDataCheck++;
		}
		if(length(tabMain.tab2.Div1.edTimeDetl8Tot.Value) > 0){	 //스타트타임 합계
			iDataCheck++;
		}		

	}

	if(iDataCheck == 0){
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return;	
	} 	 
	return true;
}


function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    var iTotAssignScr = 0;

    //평가항목
	if(tabMain.TabIndex == 0){     
		//평가항목 저장   
		sServiceName  = "d06000019-service:savePilotItemCd1";
		sInDataSet    = "dsPilotItemCd1=dsPilotItemCd1:u ";
		sOutDataSet   = "dsPilotItemCd1=dsPilotItemCd1 ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

		//스타트,주회타임배점 저장   
		sServiceName  = "d06000019-service:savePilotRoundBas2";
		sInDataSet    = "dsPilotRoundBas2=dsPilotRoundBas2:u ";
		sOutDataSet   = "dsPilotRoundBas2=dsPilotRoundBas2 ";
		for(var i=0; i<dsPilotRoundBas2.rowcount; i++) {   
			dsPilotRoundBas2.SetColumn(i, "RACER_PERIO_NO", Trim(cbPerioNo.Value));
			dsPilotRoundBas2.SetColumn(i, "ROUND", 			Trim(cbSearchRound.Value));
		}
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");  

	 	//주회타임 저장   
		sServiceName  = "d06000019-service:savePilotCcitTimeBas3";
		sInDataSet    = "dsPilotCcitTimeBas3=dsPilotCcitTimeBas3:u ";
		sOutDataSet   = "dsPilotCcitTimeBas3=dsPilotCcitTimeBas3 ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");      

		//스타트타임 저장   
		sServiceName  = "d06000019-service:savePilotStTimeBas4";
		sInDataSet    = "dsPilotStTimeBas4=dsPilotStTimeBas4:u ";
		sOutDataSet   = "dsPilotStTimeBas4=dsPilotStTimeBas4 ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

	//평가표
	}else if(tabMain.TabIndex == 1){

		var iEstmScr = 0;		
		//조종실기평가표 세부 저장
		sServiceName  = "d06000019-service:savePilotEstmDetl6";
		sInDataSet    = "dsPilotEstmDetl6=dsPilotEstmDetl6:a ";
		sOutDataSet   = "dsPilotEstmDetl6=dsPilotEstmDetl6 ";
		for(var i=0; i<dsPilotEstmDetl6.rowcount; i++) {   
			dsPilotEstmDetl6.SetColumn(i, "RACER_PERIO_NO", sDblPeriodNo);
			dsPilotEstmDetl6.SetColumn(i, "CAND_NO", sDblCandNo);
			dsPilotEstmDetl6.SetColumn(i, "ROUND", Trim(cbSearchRound.Value));
			//dsPilotEstmDetl6.SetColumn(i, "ESTM_PASS_YN", Trim(tabMain.tab2.cbDcsn.Value));	

			iEstmScr += toNumber(dsPilotEstmDetl6.GetColumn(i, "ESTM_SCR"));
		}

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

		//평가표 세부 주회타임
		sServiceName  = "d06000019-service:savePilotEstmTimeDetl7";
		sInDataSet    = "dsPilotEstmTimeDetlScr1=dsPilotEstmTimeDetlScr1:u ";
		sOutDataSet   = "dsPilotEstmTimeDetlScr1=dsPilotEstmTimeDetlScr1 ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

		//평가표 세부 스타트타임		
		sServiceName  = "d06000019-service:savePilotEstmTimeDetl8";
		sInDataSet    = "dsPilotEstmTimeDetlScr2=dsPilotEstmTimeDetlScr2:u ";
		sOutDataSet   = "dsPilotEstmTimeDetlScr2=dsPilotEstmTimeDetlScr2 ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");	


		//평가표 세부 주회기록
		sServiceName  = "d06000019-service:savePilotEstmTimeRec1";
		sInDataSet    = "dsPilotEstmTimeRec1=dsPilotEstmTimeRec1:u ";
		sOutDataSet   = "dsPilotEstmTimeRec1=dsPilotEstmTimeRec1 ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");


		//평가표 세부 스타트기록
		sServiceName  = "d06000019-service:savePilotEstmTimeRec2";
		sInDataSet    = "dsPilotEstmTimeRec2=dsPilotEstmTimeRec2:u ";
		sOutDataSet   = "dsPilotEstmTimeRec2=dsPilotEstmTimeRec2 ";

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

		iTotAssignScr = iEstmScr + toNumber(Trim(tabMain.tab2.Div0.edTimeDetl7Tot.Value)) + toNumber(Trim(tabMain.tab2.Div1.edTimeDetl8Tot.Value));
		tabMain.tab2.edTotScr.value = iTotAssignScr;	

		//평가표 저장   
		sServiceName  = "d06000019-service:savePilotEstmItem5";
		sInDataSet    = "dsPilotEstmItem5=dsPilotEstmItem5:u ";
		sOutDataSet   = "dsPilotEstmItem5=dsPilotEstmItem5 ";
		var sCandNo5  = "";

		for(var i=0; i<dsPilotEstmItem5.rowcount; i++) { 
			sCandNo5 = dsPilotEstmItem5.GetColumn(i, "CAND_NO");
			if(sDblCandNo == sCandNo5){
				dsPilotEstmItem5.SetColumn(i, "RACER_PERIO_NO", Trim(sDblPeriodNo.Value));
				dsPilotEstmItem5.SetColumn(i, "CAND_NO", sDblCandNo);			
				dsPilotEstmItem5.SetColumn(i, "ROUND", Trim(cbSearchRound.Value));
				dsPilotEstmItem5.SetColumn(i, "DT", Trim(tabMain.tab2.CalInputDt.Value));
				dsPilotEstmItem5.SetColumn(i, "ASSIGN_SCR", iTotAssignScr);

				if(dsPilotEstmItem5.GetColumn(i, "ASSIGN_SCR") >= 60) {
					dsPilotEstmItem5.SetColumn(i, "DCSN", "001");     //합격
				} else dsPilotEstmItem5.SetColumn(i, "DCSN", "003");  //불합격 

				break;
			}
		}

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

		//평가일 저장
		sServiceName  = "d06000019-service:savePilotEstmItemDt";
		sInDataSet    = "dsPilotItemDt=dsPilotItemDt:u ";
		sOutDataSet   = "dsPilotItemDt=dsPilotItemDt ";
		for(var i=0; i<dsPilotItemDt.rowcount; i++) { 
			dsPilotItemDt.SetColumn(i, "RACER_PERIO_NO", Trim(sDblPeriodNo.Value));
			dsPilotItemDt.SetColumn(i, "ROUND", Trim(cbSearchRound.Value));
			dsPilotItemDt.SetColumn(i, "DT", Trim(tabMain.tab2.CalInputDt.Value));
			break;
		}	

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

	}
}


