	} else if(tabMain.TabIndex == 1){
	    var sExcelList = "tabMain.tab2.grdPilotEstmItem5:평가표|tabMain.tab2.grdPilotEstmDetl6:세부평가|tabMain.tab2.grdPilotEstmTimeDetl7:주회타임|tabMain.tab2.grdPilotEstmTimeDetl8:스타트타임";
		fcd_OpenExcelListExport(sExcelList);
	}else{
		alert("엑셀 기능이 지원되지 않습니다.");
	}
}

