/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    var sInDataSet   = "";
    var sOutDataSet  = "";
	var sDt = "";

	var iTimeDetl7TotScr	= 0;
	var iTimeDetl8TotScr	= 0;
	var i7AssignScrTo		= 0;
	var i8AssignScr			= 0;
	var iEstmDetl6001Tot	= 0;
	var iEstmDetl6002Tot	= 0;
	var iEstmDetl6003Tot	= 0;
	var iEstmDetl6004Tot	= 0;
	var sEstmItemCd			= "";
    switch(sServiceName) {
        case "d06000019-service:searchPerioNo" :   
			fcsearch();
            break;
        //   평가표 조회 후 콜백 처리    
        case "d06000019-service:searchPilotEstmItem5" :
			if(dsPilotEstmItem5.GetRowCount() > 0) {
				dsPilotEstmItem5_OnRowPosChanged("", "", 0);
			}	
            break;  
        case "d06000019-service:searchPilotEstmDetl6" :
			tabMain.tab2.stcNmKor.Text =  "[" + dsPilotEstmDetl6.getColumn(0,"NM_KOR") + "]";

			for(var i=0; i<dsPilotEstmDetl6.rowcount; i++) {
				iTotScr += toNumber(dsPilotEstmDetl6.GetColumn(i, "ESTM_SCR"));
				sEstmItemCd = dsPilotEstmDetl6.GetColumn(i, "ESTM_ITEM_CD");
				if(sEstmItemCd =="001"){
					iEstmDetl6001Tot += toNumber(dsPilotEstmDetl6.GetColumn(i, "ASSIGN_SCR_LIMIT"));
				}else if(sEstmItemCd =="002"){
					iEstmDetl6002Tot += toNumber(dsPilotEstmDetl6.GetColumn(i, "ASSIGN_SCR_LIMIT"));
				}else if(sEstmItemCd =="003"){
					iEstmDetl6003Tot += toNumber(dsPilotEstmDetl6.GetColumn(i, "ASSIGN_SCR_LIMIT"));
				}else if(sEstmItemCd =="004"){
					iEstmDetl6004Tot += toNumber(dsPilotEstmDetl6.GetColumn(i, "ASSIGN_SCR_LIMIT"));
				}
			}
			tabMain.tab2.edEstmDetl6001Tot.value = iEstmDetl6001Tot;
			tabMain.tab2.edEstmDetl6002Tot.value = iEstmDetl6002Tot;
			tabMain.tab2.edEstmDetl6003Tot.value = iEstmDetl6003Tot;
			tabMain.tab2.edEstmDetl6004Tot.value = iEstmDetl6004Tot;				

			//평가표 세부 주회타임
			sServiceName = "d06000019-service:searchPilotEstmTimeDetl7";
			sInDataSet   = "";
			sOutDataSet  = " dsPilotEstmTimeDetl7=dsPilotEstmTimeDetl7 ";
			sOutDataSet += " dsPilotEstmTimeDetlScr1=dsPilotEstmTimeDetlScr1 ";
			sOutDataSet += " dsPilotEstmTimeRec1=dsPilotEstmTimeRec1 ";
			fcd_AddParam("racerPerioNo", sDblPeriodNo);
			fcd_AddParam("candNo", sDblCandNo);
			fcd_AddParam("round", Trim(cbSearchRound.Value));
			fcd_AddParam("estmId", gdsUser.getColumn(0,"USER_ID"));
			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
            break;

        case "d06000019-service:searchPilotEstmTimeDetl7" :
			i7AssignScrTo = toNumber(dsPilotRoundBas2.GetColumn(0,"CCIT_SCR"));
			tabMain.tab2.Div0.edCcitTimeScr.value = i7AssignScrTo;

			if(parseInt(tabMain.tab2.Div0.edCcitTimeScr.value) == 0) {
				tabMain.tab2.Div0.Visible = "false";
			} else {
				tabMain.tab2.Div0.Top = "285";
				tabMain.tab2.Div0.Left = "352";
				tabMain.tab2.Div0.Visible = "true";
			}

			if(dsPilotEstmTimeDetlScr1.rowcount == 0 && parseInt(tabMain.tab2.Div0.edCcitTimeScr.value) > 0 ) {
				dsPilotEstmTimeDetlScr1.AddRow();
				dsPilotEstmTimeDetlScr1.setColumn(dsPilotEstmTimeDetlScr1.row,"RACER_PERIO_NO",sDblPeriodNo);
				dsPilotEstmTimeDetlScr1.setColumn(dsPilotEstmTimeDetlScr1.row,"CAND_NO",sDblCandNo);
				dsPilotEstmTimeDetlScr1.setColumn(dsPilotEstmTimeDetlScr1.row,"ROUND",Trim(cbSearchRound.Value));
				dsPilotEstmTimeDetlScr1.setColumn(dsPilotEstmTimeDetlScr1.row,"TIME_GBN","C");
				dsPilotEstmTimeDetlScr1.setColumn(dsPilotEstmTimeDetlScr1.row,"ESTM_ID",gdsUser.getColumn(0,"USER_ID"));
				dsPilotEstmTimeDetlScr1.setColumn(dsPilotEstmTimeDetlScr1.row,"ESTM_SCR","0");

			}

			iTimeDetl7TotScr = dsPilotEstmTimeDetlScr1.GetColumn(0,"ESTM_SCR");
			iTotScr += iTimeDetl7TotScr;

			//평가표 세부 스타트타임
			sServiceName = "d06000019-service:searchPilotEstmTimeDetl8";
			sInDataSet   = "";
			sOutDataSet  = " dsPilotEstmTimeDetl8=dsPilotEstmTimeDetl8 ";
			sOutDataSet += " dsPilotEstmTimeDetlScr2=dsPilotEstmTimeDetlScr2 ";
			sOutDataSet += " dsPilotEstmTimeRec2=dsPilotEstmTimeRec2 ";
			fcd_AddParam("racerPerioNo", sDblPeriodNo);
			fcd_AddParam("candNo", sDblCandNo);
			fcd_AddParam("round", Trim(cbSearchRound.Value));
			fcd_AddParam("estmId", gdsUser.getColumn(0,"USER_ID"));
			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);				
            break; 

        case "d06000019-service:searchPilotEstmTimeDetl8" :
			i8AssignScr = toNumber(dsPilotRoundBas2.GetColumn(0,"ST_TIME_SCR"));

			tabMain.tab2.Div1.edStTimeScr.value = i8AssignScr;			

			if(parseInt(tabMain.tab2.Div1.edStTimeScr.value) == 0) {
				tabMain.tab2.Div1.Visible = "false";
			} else {
				tabMain.tab2.Div1.Top = "285";
				tabMain.tab2.Div1.Left = "352";
				tabMain.tab2.Div1.Visible = "true";
			}

			if(dsPilotEstmTimeDetlScr2.rowcount == 0 && parseInt(tabMain.tab2.Div1.edStTimeScr.value) > 0) {
				dsPilotEstmTimeDetlScr2.AddRow();
				dsPilotEstmTimeDetlScr2.setColumn(dsPilotEstmTimeDetlScr2.row,"RACER_PERIO_NO",sDblPeriodNo);
				dsPilotEstmTimeDetlScr2.setColumn(dsPilotEstmTimeDetlScr2.row,"CAND_NO",sDblCandNo);
				dsPilotEstmTimeDetlScr2.setColumn(dsPilotEstmTimeDetlScr2.row,"ROUND",Trim(cbSearchRound.Value));
				dsPilotEstmTimeDetlScr2.setColumn(dsPilotEstmTimeDetlScr2.row,"TIME_GBN","S");
				dsPilotEstmTimeDetlScr2.setColumn(dsPilotEstmTimeDetlScr2.row,"ESTM_ID",gdsUser.getColumn(0,"USER_ID"));
				dsPilotEstmTimeDetlScr2.setColumn(dsPilotEstmTimeDetlScr2.row,"ESTM_SCR","0");
			}

			iTimeDetl8TotScr = dsPilotEstmTimeDetlScr2.getColumn(0,"ESTM_SCR");;
			iTotScr += iTimeDetl8TotScr;
//			tabMain.tab2.div1.edTotScr.value = iTotScr;
			iTotScr = 0;
            break;                           

        case "d06000019-service:searchPilotItemCd1" :
			if(dsPilotItemCd1.rowcount > 0)
			{
				tabMain.tab1.edItemScr.Text = dsPilotItemCd1.Sum("ASSIGN_SCR_LIMIT");
			}
			else
			{
				tabMain.tab1.edItemScr.Text = "";
			}
			break;

		case "d06000019-service:searchPilotStTimeBas4" :
			if(dsPilotItemCd1.rowcount + dsPilotCcitTimeBas3.rowcount + dsPilotStTimeBas4.rowcount == 0) {
				if(trim(cbSearchRound.Value) == '1') { //1차의 데이터가 없는 경우는 전차수 복사
					tabMain.tab1.btnPrevPerioCp.Enable = true;
				} else {	
					tabMain.tab1.btnPrevCp.Enable = true;
				}
			} else {
				tabMain.tab1.btnPrevCp.Enable = false;
				tabMain.tab1.btnPrevPerioCp.Enable = false;
			}
			break;

		case "d06000019-service:searchPrevPilotItemCd1" :
			tabMain.tab1.btnPrevCp.Enable = false;
			tabMain.tab1.btnPrevPerioCp.Enable = false;
			if(dsPrevPilotItemCd1.rowcount > 0) {
				tabMain.tab1.grdPilotItemCd1.Visible = false;
				for(var i=0; i < dsPrevPilotItemCd1.rowcount; i++) {
					dsPilotItemCd1.AddRow();
					for(var j=0; j < dsPrevPilotItemCd1.colcount; j++) {
						dsPilotItemCd1.SetColumn(dsPilotItemCd1.row,j,dsPrevPilotItemCd1.getColumn(i,j));
					}
					dsPilotItemCd1.SetColumn(dsPilotItemCd1.row,"RACER_PERIO_NO",cbPerioNo.Value);
					dsPilotItemCd1.SetColumn(dsPilotItemCd1.row,"ROUND",cbSearchRound.Value);
				}
				tabMain.tab1.grdPilotItemCd1.Visible = true;
			}
			if(dsPrevPilotRoundBas2.rowcount > 0) {
				dsPilotRoundBas2.DeleteAll();
				for(var i=0; i < dsPrevPilotRoundBas2.rowcount; i++) {
					dsPilotRoundBas2.AddRow();
					for(var j=0; j < dsPrevPilotRoundBas2.colcount; j++) {
						dsPilotRoundBas2.SetColumn(dsPilotRoundBas2.row,j,dsPrevPilotRoundBas2.getColumn(i,j));
					}
					dsPilotRoundBas2.SetColumn(dsPilotRoundBas2.row,"RACER_PERIO_NO",cbPerioNo.Value);
					dsPilotRoundBas2.SetColumn(dsPilotRoundBas2.row,"ROUND",cbSearchRound.Value);
				}
			}
			if(dsPrevPilotCcitTimeBas3.rowcount > 0) {
				tabMain.tab1.grdPilotCcitTimeBas3.Visible = false;
				for(var i=0; i < dsPrevPilotCcitTimeBas3.rowcount; i++) {
					dsPilotCcitTimeBas3.AddRow();
					for(var j=0; j < dsPrevPilotCcitTimeBas3.colcount; j++) {
						dsPilotCcitTimeBas3.SetColumn(dsPilotCcitTimeBas3.row,j,dsPrevPilotCcitTimeBas3.getColumn(i,j));
					}
					dsPilotCcitTimeBas3.SetColumn(dsPilotCcitTimeBas3.row,"RACER_PERIO_NO",cbPerioNo.Value);
					dsPilotCcitTimeBas3.SetColumn(dsPilotCcitTimeBas3.row,"ROUND",cbSearchRound.Value);
				}
				tabMain.tab1.grdPilotCcitTimeBas3.Visible = true;
			}
			if(dsPrevPilotStTimeBas4.rowcount > 0) {
				tabMain.tab1.grdPilotStTimeBas4.Visible = false;
				for(var i=0; i < dsPrevPilotStTimeBas4.rowcount; i++) {
					dsPilotStTimeBas4.AddRow();
					for(var j=0; j < dsPrevPilotStTimeBas4.colcount; j++) {
						dsPilotStTimeBas4.SetColumn(dsPilotStTimeBas4.row,j,dsPrevPilotStTimeBas4.getColumn(i,j));
					}
					dsPilotStTimeBas4.SetColumn(dsPilotStTimeBas4.row,"RACER_PERIO_NO",cbPerioNo.Value);
					dsPilotStTimeBas4.SetColumn(dsPilotStTimeBas4.row,"ROUND",cbSearchRound.Value);
				}
				tabMain.tab1.grdPilotStTimeBas4.Visible = true;
			}
			break;

		case "d06000019-service:searchPilotRoundBas2" :

			if(dsPilotRoundBas2.rowcount == 0) {
				dsPilotRoundBas2.AddRow();
			}
			break;
		case "d06000019-service:searchPrevPilotItemDt" :
			if(dsPilotItemDt.GetRowCount() == 0) dsPilotItemDt.AddRow();
			break;
        case "d06000019-service:savePilotEstmItem5" :
			fcsearch();
        default :
            break;
    }
    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}


