/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리
	if (obj.GetForm().id == "divBtnRow1"){
		dsPilotItemCd1.AddRow();
		dsPilotItemCd1.SetColumn(dsPilotItemCd1.row,"RACER_PERIO_NO",cbPerioNo.Value);
		dsPilotItemCd1.SetColumn(dsPilotItemCd1.row,"ROUND",cbSearchRound.Value);
	}else if(obj.GetForm().id == "divBtnRow2"){
		dsPilotRoundBas2.AddRow();
		dsPilotRoundBas2.SetColumn(dsPilotRoundBas2.row,"RACER_PERIO_NO",cbPerioNo.Value);
		dsPilotRoundBas2.SetColumn(dsPilotRoundBas2.row,"ROUND",cbSearchRound.Value);
	}else if(obj.GetForm().id == "divBtnRow3"){
		dsPilotCcitTimeBas3.AddRow();
		dsPilotCcitTimeBas3.SetColumn(dsPilotCcitTimeBas3.row,"RACER_PERIO_NO",cbPerioNo.Value);
		dsPilotCcitTimeBas3.SetColumn(dsPilotCcitTimeBas3.row,"ROUND",cbSearchRound.Value);
	}else if(obj.GetForm().id == "divBtnRow4"){
        dsPilotStTimeBas4.AddRow();
		dsPilotStTimeBas4.SetColumn(dsPilotStTimeBas4.row,"RACER_PERIO_NO",cbPerioNo.Value);
		dsPilotStTimeBas4.SetColumn(dsPilotStTimeBas4.row,"ROUND",cbSearchRound.Value);
	}else if(obj.GetForm().id == "divBtnRow5"){
		if(sDblCandNo == "") {
			alert("평가할 후보생을 선택하세요.");
			return;
		}
		dsPilotEstmTimeRec1.AddRow();
		dsPilotEstmTimeRec1.SetColumn(dsPilotEstmTimeRec1.row,"RACER_PERIO_NO",sDblPeriodNo);
		dsPilotEstmTimeRec1.setColumn(dsPilotEstmTimeRec1.row,"CAND_NO",sDblCandNo);
		dsPilotEstmTimeRec1.setColumn(dsPilotEstmTimeRec1.row,"ROUND",Trim(cbSearchRound.Value));
		dsPilotEstmTimeRec1.setColumn(dsPilotEstmTimeRec1.row,"TIME_GBN","C");
		dsPilotEstmTimeRec1.setColumn(dsPilotEstmTimeRec1.row,"ESTM_ID",gdsUser.getColumn(0,"USER_ID"));
		dsPIlotEstmTimeRec1.setColumn(dsPilotEstmTimeRec1.row,"ESTM_CNT",dsPilotEstmTimeRec1.row+1);
	}else if(obj.GetForm().id == "divBtnRow6"){
		if(sDblCandNo == "") {
			alert("평가할 후보생을 선택하세요.");
			return;
		}
		dsPilotEstmTimeRec2.AddRow();
		dsPilotEstmTimeRec2.SetColumn(dsPilotEstmTimeRec2.row,"RACER_PERIO_NO",sDblPeriodNo);
		dsPilotEstmTimeRec2.setColumn(dsPilotEstmTimeRec2.row,"CAND_NO",sDblCandNo);
		dsPilotEstmTimeRec2.setColumn(dsPilotEstmTimeRec2.row,"ROUND",Trim(cbSearchRound.Value));
		dsPilotEstmTimeRec2.setColumn(dsPilotEstmTimeRec2.row,"TIME_GBN","S");
		dsPilotEstmTimeRec2.setColumn(dsPilotEstmTimeRec2.row,"ESTM_ID",gdsUser.getColumn(0,"USER_ID"));
		dsPilotEstmTimeRec2.setColumn(dsPilotEstmTimeRec2.row,"ESTM_CNT",dsPilotEstmTimeRec2.row+1);
    }     
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if(obj.GetForm().id == "divBtnRow1" ) {
		if ( !fnc_DeleteCheck(dsPilotItemCd1, "CHK", "1") ) return;
		fcInit();
		tabMain.tab1.grdPilotItemCd1.SetFocus(); //Record 삭제 후 Grid로 Focus
		tabMain.tab1.edItemScr.Text = dsPilotItemCd1.Sum("ASSIGN_SCR_LIMIT");
		break;  	
	}else if(obj.GetForm().id == "divBtnRow2" ) {
		if ( !fnc_DeleteCheck(dsPilotRoundBas2, "CHK", "1") ) return;
		fcInit();
		tabMain.tab1.grdPilotRoundBas2.SetFocus(); //Record 삭제 후 Grid로 Focus
		break;
	}else if(obj.GetForm().id == "divBtnRow3" ) {
		if ( !fnc_DeleteCheck(dsPilotCcitTimeBas3, "CHK", "1") ) return;
		fcInit();
		tabMain.tab1.grdPilotCcitTimeBas3.SetFocus(); //Record 삭제 후 Grid로 Focus
		break;	
	}else if(obj.GetForm().id == "divBtnRow4" ) {
		if ( !fnc_DeleteCheck(dsPilotStTimeBas4, "CHK", "1") ) return;
		fcInit();
		tabMain.tab1.grdPilotStTimeBas4.SetFocus(); //Record 삭제 후 Grid로 Focus
		break;	
	}else if(obj.GetForm().id == "divBtnRow5" ) {
		if ( !fnc_DeleteCheck(dsPilotEstmTimeRec1, "CHK", "1") ) return;
		fcInit();
		tabMain.tab2.Div0.grdPilotEstmTimeRec1.SetFocus(); //Record 삭제 후 Grid로 Focus
		break;
	}else if(obj.GetForm().id == "divBtnRow6" ) {
		if ( !fnc_DeleteCheck(dsPilotEstmTimeRec2, "CHK", "1") ) return;
		fcInit();
		tabMain.tab2.Div1.grdPilotEstmTimeRec2.SetFocus(); //Record 삭제 후 Grid로 Focus
		break;
	}
}

