function dsPilotItemCd1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	tabMain.tab1.edItemScr.Text = dsPilotItemCd1.Sum("ASSIGN_SCR_LIMIT");
}

function dsPilotEstmDetl6_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(dsPilotEstmDetl6.getColumn(nRow, "ESTM_SCR") > dsPilotEstmDetl6.getColumn(nRow, "ASSIGN_SCR_LIMIT")) {
		alert("평가는 배점한도보다 클수 없습니다.");
		dsPilotEstmDetl6.setColumn(nRow, "ESTM_SCR",varOldValue);
	} else {
		tabMain.tab2.edTimeDetl6Tot.Text = dsPilotEstmDetl6.Sum("ESTM_SCR");
		tabMain.tab2.edTotScr.Text = parseInt(tabMain.tab2.edTimeDetl6Tot.Text) + parseInt(tabMain.tab2.Div0.edTimeDetl7Tot.Text) + parseInt(tabMain.tab2.Div1.edTimeDetl8Tot.Text);	
	}
}

function tabMain_tab2_edTimeDetl6Tot_OnChanged(obj,strText)
{
	tabMain.tab2.edTotScr.Text = parseInt(tabMain.tab2.edTimeDetl6Tot.Text) + parseInt(tabMain.tab2.Div0.edTimeDetl7Tot.Text) + parseInt(tabMain.tab2.Div1.edTimeDetl8Tot.Text);	
}

function tabMain_tab2_edTimeDetl7Tot_OnChanged(obj,strText)
{
	tabMain.tab2.edTotScr.Text = parseInt(tabMain.tab2.edTimeDetl6Tot.Text) + parseInt(tabMain.tab2.Div0.edTimeDetl7Tot.Text) + parseInt(tabMain.tab2.Div1.edTimeDetl8Tot.Text);
}

function tabMain_tab2_edTimeDetl8Tot_OnChanged(obj,strText)
{
	tabMain.tab2.edTotScr.Text = parseInt(tabMain.tab2.edTimeDetl6Tot.Text) + parseInt(tabMain.tab2.Div0.edTimeDetl7Tot.Text) + parseInt(tabMain.tab2.Div1.edTimeDetl8Tot.Text);
}


function dsPilotEstmDetl6_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	tabMain.tab2.edTimeDetl6Tot.Text = dsPilotEstmDetl6.Sum("ESTM_SCR");
	tabMain.tab2.edTotScr.Text = parseInt(tabMain.tab2.edTimeDetl6Tot.Text) + parseInt(tabMain.tab2.Div0.edTimeDetl7Tot.Text) + parseInt(tabMain.tab2.Div1.edTimeDetl8Tot.Text);	
}


