function tabMain_tab1_btnPrevCp_OnClick(obj)
{
	//평가항목 조회
	sServiceName = "d06000019-service:searchPrevPilotItemCd1";
	sInDataSet   = "";
	sOutDataSet  = " dsPrevPilotItemCd1=dsPrevPilotItemCd1 ";
	sOutDataSet += " dsPrevPilotRoundBas2=dsPrevPilotRoundBas2 ";
	sOutDataSet += " dsPrevPilotCcitTimeBas3=dsPrevPilotCcitTimeBas3 ";
