function fcPrint(){
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";          //레포트 파일명
	var sDataSet  = "";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "";

	if (tabMain.TabIndex == 1){
			 sRptNm    = "d06000019_1.jrf";          //레포트 파일명
			 sDataSet  = " dsOutPilotEstmItemPrint=dsOutPilotEstmItemPrint ";    //"UBI 쿼리명, MIP Dataset명"
			 sArg      = "PerioNo=" + cbPerioNo.Text+ " Round="+cbSearchRound.Text ;;
			 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}else if (tabMain.TabIndex == 2){ 
			 sRptNm    = "d06000019_2.jrf";          //레포트 파일명
			 sDataSet  = " dsOutPilotEstmItemSumPrint=dsOutPilotEstmItemSumPrint ";    //"UBI 쿼리명, MIP Dataset명"
			 sArg      = "PerioNo=" + cbPerioNo.Text+ " Round="+cbSearchRound.Text ;;
			 sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)
			 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
	}
}

function dsPilotEstmTimeDetlScr_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	//스타트타임
    switch(obj.id) {
        case "dsPilotEstmTimeDetlScr1" :	//주회타임
			if(dsPilotEstmTimeDetlScr1.getColumn(nRow,"ESTM_SCR")>tabMain.tab2.Div0.edCcitTimeScr.text) {
				alert("평가는 배점한도보다 클수 없습니다.");
				dsPilotEstmTimeDetlScr1.setColumn(nRow,"ESTM_SCR",varOldValue);
			}
			break;
        case "dsPilotEstmTimeDetlScr2" :	//스타트타임
			if(dsPilotEstmTimeDetlScr2.getColumn(nRow,"ESTM_SCR")>tabMain.tab2.Div1.edStTimeScr.text) {
				alert("평가는 배점한도보다 클수 없습니다.");
				dsPilotEstmTimeDetlScr2.setColumn(nRow,"ESTM_SCR",varOldValue);
			}
			break;
        default :
            break;			
	}
}


function dsPilotEstmItem5_OnRowPosChanged(obj,nOldRow,nRow)
{
    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";

	for(var i=0; i<dsPilotEstmItem5.rowcount; i++) {
		if(i == nRow){
			sDblPeriodNo 	= dsPilotEstmItem5.GetColumn(i, "RACER_PERIO_NO");
			sDblCandNo		= dsPilotEstmItem5.GetColumn(i, "CAND_NO");
			sDblDcsn		= dsPilotEstmItem5.GetColumn(i, "DCSN");
			break;
		}
	}

	//조종실기평가표 세부조회
	sServiceName = "d06000019-service:searchPilotEstmDetl6";
	sInDataSet   = "";
	sOutDataSet  = " dsPilotEstmDetl6=dsPilotEstmDetl6 ";
	fcd_AddParam("racerPerioNo", sDblPeriodNo);
	fcd_AddParam("candNo", sDblCandNo);
	fcd_AddParam("round", Trim(cbSearchRound.Value));
	fcd_AddParam("estmId", gdsUser.getColumn(0,"USER_ID"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);			
}


