function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
		Div0.cbRound1.value = null;
		Div0.cbRaceGbn1.value = null;
		Div0.edRaceCount1.Text = "";
		Div0.edRaceTime1.text = "";

		Div0.cbRound2.value = null;
		Div0.cbRaceGbn2.value = null;
		Div0.edRaceCount2.Text = "";
		Div0.edRaceTime2.text = "";

		Div0.cbRound3.value = null;
		Div0.cbRaceGbn3.value = null;
		Div0.edRaceCount3.Text = "";
		Div0.edRaceTime3.text = "";

		Div0.cbRound2.value = null;
		Div0.cbRaceGbn4.value = null;
		Div0.edRaceCount4.Text = "";
		Div0.edRaceTime4.text = "";

		Div0.cbRound5.value = null;
		Div0.cbRaceGbn5.value = null;
		Div0.edRaceCount5.Text = "";
		Div0.edRaceTime5.text = "";
    }

    dsOutCandRace1.ClearData();	
	dsOutCandRace2.ClearData();	
	dsOutCandRace3.ClearData();
	dsOutCandRace4.ClearData();	
	dsOutCandRace5.ClearData();	

	dsCandIdent_1.ClearData();
	dsCandIdent_2.ClearData();
	dsCandIdent_3.ClearData();
	dsCandIdent_4.ClearData();
    dsCandIdent_5.ClearData();
}

function fcToExcel(obj) {

	var sExcelList = "";

	if(Radio0.Value = "0001"){
		sExcelList = "Div0.grdRace1:∂ÛøÓµÂ(1)|Div0.grdRace2:∂ÛøÓµÂ(2)|Div0.grdRace3:∂ÛøÓµÂ(3)|Div0.grdRace4:∂ÛøÓµÂ(4)|Div0.grdRace5:∂ÛøÓµÂ(5)";
	}else{
		sExcelList = "Div0.grdRace1:∂ÛøÓµÂ(6)|Div0.grdRace2:∂ÛøÓµÂ(7)|Div0.grdRace3:∂ÛøÓµÂ(8)|Div0.grdRace4:∂ÛøÓµÂ(9)|Div0.grdRace5:∂ÛøÓµÂ(10)";
	}
	fcd_OpenExcelListExport(sExcelList);
}

