function fcSearchCheck(obj) {
//    edSCfrntCd.Text = Trim(edSCfrntCd.Text);
	fcInit(obj);	

	if(length(cbPerioNo.Value) <= 0){	
			//±‚ºˆ « ºˆ ¿˙¿Â
			fnc_Message(SNIS_CAN_E001, "N", "±‚ºˆ∏¶ ");
			return;
	}

	return true;
}


//¡∂»∏πˆ∆∞
function fcSearch(obj) {

	//alert("obj id : " + obj.id);

	//alert("fcSearchCheck()==>"+fcSearchCheck());
    if ( fcSearchCheck(obj) == 0 || fcSearchCheck(obj) == "") return;

    fcCandIdent();

    fcBoat();

    fcMotor();


    fcChulju();

}


//±‚ºˆƒﬁ∫∏
function fcPerioNo() {
	var racerPerioNo = null;
	var sServiceName = "d06000023-service:searchPerioNo";
    var sInDataSet   = "";
    var sOutDataSet  = " dsPerioNo=dsPerioNo "; 
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

