//¿˙¿Âπˆ∆∞≈¨∏Ø
function fcSave(obj) {

	if ( !fcSaveCheck() ) return;
	//alert("fcSaveCheck »ƒ");


	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";   

    sServiceName  = "d06000023-service:saveChulju";
	sInDataSet    = " dsOutCandRace1=dsOutCandRace1:u dsOutCandRace2=dsOutCandRace2:u dsOutCandRace3=dsOutCandRace3:u dsOutCandRace4=dsOutCandRace4:u dsOutCandRace5=dsOutCandRace5:u";
	sOutDataSet   = " dsOutCandRace1=dsOutCandRace1 dsOutCandRace2=dsOutCandRace2 dsOutCandRace3=dsOutCandRace3 dsOutCandRace4=dsOutCandRace4 dsOutCandRace5=dsOutCandRace5";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);

	//trace(dsOutCandRace1.savexml());
}

function fcSaveCheck(obj) {

	//∫Ø∞Êµ» ¿⁄∑·∞° ¿÷¥¬¡ˆ √º≈©

	// « ºˆ«◊∏Ò√º≈©
	var oDataSet 			= dsWrtnEstm1;
	var sMandatoryColID   	= null;
	var sMandatoryColName 	= null;
	var oGrid             	= null;


	if ( !fnc_DatasetChangeCheck(dsOutCandRace1) 
		&& !fnc_DatasetChangeCheck(dsOutCandRace2) 
		&& !fnc_DatasetChangeCheck(dsOutCandRace3)
		&& !fnc_DatasetChangeCheck(dsOutCandRace4)
		&& !fnc_DatasetChangeCheck(dsOutCandRace5)) 
	{
		//¿˙¿Â «“ ¿⁄∑·∞° æ¯¿Ω
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	if(length(cbPerioNo.Value) <= 0 || length(calRaceDate.Value) < 1 ){	
		//±‚ºˆ « ºˆ ¿˙¿Â
		fnc_Message(SNIS_CAN_E001, "N", "±‚ºˆøÕ ¿œ¿⁄¥¬ ");
		return false;
	}



	if ( fnc_DatasetChangeCheck(dsOutCandRace1) ) {
		for (var i=0; i<dsOutCandRace1.count; i++) {
			if ( toString( dsOutCandRace1.GetColumn(i,"TEAM_NO") ).length < 1 ) {
				fnc_Message(SNIS_CAN_E004, "N");
				return false;
				break;
			} 
		}

	}

	if ( fnc_DatasetChangeCheck(dsOutCandRace2) ) {
		for (var i=0; i<dsOutCandRace2.count; i++) {
			if ( toString( dsOutCandRace2.GetColumn(i,"TEAM_NO") ).length < 1 ) {
				fnc_Message(SNIS_CAN_E004, "N");
				return false;
				break;
			} 
		}

	}

	if ( fnc_DatasetChangeCheck(dsOutCandRace1) ) {
		for (var i=0; i<dsOutCandRace3.count; i++) {
			if ( toString( dsOutCandRace3.GetColumn(i,"TEAM_NO") ).length < 1 ) {
				fnc_Message(SNIS_CAN_E004, "N");
				return false;
				break;
			} 
		}

	}

	if ( fnc_DatasetChangeCheck(dsOutCandRace1) ) {
		for (var i=0; i<dsOutCandRace4.count; i++) {
			if ( toString( dsOutCandRace4.GetColumn(i,"TEAM_NO") ).length < 1 ) {
				fnc_Message(SNIS_CAN_E004, "N");
				return false;
				break;
			} 
		}

	}

	if ( fnc_DatasetChangeCheck(dsOutCandRace1) ) {
		for (var i=0; i<dsOutCandRace5.count; i++) {
			if ( toString( dsOutCandRace5.GetColumn(i,"TEAM_NO") ).length < 1 ) {
				fnc_Message(SNIS_CAN_E004, "N");
				return false;
				break;
			} 
		}

	}


	return true;
}


