/*-------------------------------------------------------------------------------------------------+
>>  Transaction »ƒ √≥∏Æ «ÿæﬂ «“ ≥ªøÎ!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

     switch(sServiceName) {
		case "d06000023-service:searchChuljuCand" :
			//alert("-------------------");
			var cnt = dsCandIdent_2.Copy("dsCandIdent_1");
			var cnt = dsCandIdent_3.Copy("dsCandIdent_1");
			var cnt = dsCandIdent_4.Copy("dsCandIdent_1");
			var cnt = dsCandIdent_5.Copy("dsCandIdent_1");
			break;



		default :
			break;

	 }

	 if (sServiceName=="d06000023-service:searchChulju1")
	 {
			//alert("maindt : "+maindt);

			var maindt = "";
			for(var i=0; i<dsOutCandRace1.count; i++){
				maindt = dsOutCandRace1.GetColumn(i,"DT");
				if ( maindt.length > 0 ){ 
					calRaceDate.value = maindt;
					break;
				}
			}
			//alert("maindt : "+maindt);

	 }	


     if (sServiceName=="d06000023-service:searchChulju1")
	 {
			var round1 = "";
			for(var i=0; i<dsOutCandRace1.count; i++){
				round1 = toString( dsOutCandRace1.GetColumn(i,"ROUND") );

				if ( round1.length > 0 ){ 
					//alert("++++++++++++++++++++++++");
					Div0.cbRound1.value = round1;
					break;
				}
			}


			var gbn1 = "";
			for(var i=0; i<dsOutCandRace1.count; i++){
				gbn1 = dsOutCandRace1.GetColumn(i,"GBN");
				if ( gbn1.length > 0 ){ 
					Div0.cbRaceGbn1.value = gbn1;
					break;
				}
			}


			var ccit1 = "";
			for(var i=0; i<dsOutCandRace1.count; i++){
				ccit1 = toString( dsOutCandRace1.GetColumn(i,"CCIT_CNT") );
				if ( ccit1.length > 0 ){ 
					Div0.edRaceCount1.Text = ccit1;
					break;
				}
			}


			var bt1 = "";
			for(var i=0; i<dsOutCandRace1.count; i++){
				bt1 = toString(dsOutCandRace1.GetColumn(i,"BOAT_TIME") );
				//alert(bt1);
				if ( bt1.length > 0 ){ 
					//Div0.edRaceTime1.Text = bt1;
					Div0.edRaceTime1.text = bt1;
					break;
				}
			}

	 } 

     if (sServiceName=="d06000023-service:searchChulju2")
	 {
			var round2 = "";
			for(var i=0; i<dsOutCandRace2.count; i++){
				round2 = toString( dsOutCandRace2.GetColumn(i,"ROUND") );
				if ( round2.length > 0){ 
					Div0.cbRound2.value = round2;
					break;
				}
			}


			var gbn2 = "";
			for(var i=0; i<dsOutCandRace2.count; i++){

				gbn2 = dsOutCandRace2.GetColumn(i,"GBN");
				//alert("out : "+gbn2 + "  ±Ê¿Ã  " +gbn2.length );
				if ( gbn2.length > 0 ){ 
					Div0.cbRaceGbn2.value = gbn2;
					break;
				}
			}



			var ccit2 = "";
			for(var i=0; i<dsOutCandRace2.count; i++){
				ccit2 = toString( dsOutCandRace2.GetColumn(i,"CCIT_CNT") );
				if ( ccit2.length > 0 ){ 
					Div0.edRaceCount2.Text = ccit2;
					break;
				}
			}


			var bt2 = "";
			for(var i=0; i<dsOutCandRace2.count; i++){
				bt2 = toString( dsOutCandRace2.GetColumn(i,"BOAT_TIME") );
				if ( bt2.length > 0 ){ 
					Div0.edRaceTime2.text = bt2;
					break;
				}
			}

	 } 

     if (sServiceName=="d06000023-service:searchChulju3")
	 {
			var round3 = "";
			for(var i=0; i<dsOutCandRace3.count; i++){
				round3 = toString( dsOutCandRace3.GetColumn(i,"ROUND") );
				if ( round3.length > 0 ){ 
					Div0.cbRound3.value = round3;
					break;
				}
			}


			var gbn3 = "";
			for(var i=0; i<dsOutCandRace3.count; i++){
				gbn3 = dsOutCandRace3.GetColumn(i,"GBN");
				if ( gbn3.length > 0 ){ 
					Div0.cbRaceGbn3.value = gbn3;
					break;
				}
			}


			var ccit3 = "";
			for(var i=0; i<dsOutCandRace3.count; i++){
				ccit3 = toString( dsOutCandRace3.GetColumn(i,"CCIT_CNT") );
				if ( ccit3.length > 0 ){ 
					Div0.edRaceCount3.Text = ccit3;
					break;
				}
			}


			var bt3 = "";
			for(var i=0; i<dsOutCandRace3.count; i++){
				bt3 = toString( dsOutCandRace3.GetColumn(i,"BOAT_TIME") );
				if ( bt3.length > 0 ){ 
					Div0.edRaceTime3.text = bt3;
					break;
				}
			}

	 } 

     if (sServiceName=="d06000023-service:searchChulju4")
	 {
			var round4 = "";
			for(var i=0; i<dsOutCandRace4.count; i++){
				round4 = toString( dsOutCandRace4.GetColumn(i,"ROUND") );
				if ( round4.length > 0 ){ 
					Div0.cbRound2.value = round4;
					break;
				}
			}


			var gbn4 = "";
			for(var i=0; i<dsOutCandRace4.count; i++){
				gbn4 = dsOutCandRace4.GetColumn(i,"GBN");
				if ( gbn4.length > 0 ){ 
					Div0.cbRaceGbn4.value = gbn4;
					break;
				}
			}


			var ccit4 = "";
			for(var i=0; i<dsOutCandRace4.count; i++){
				ccit4 = toString( dsOutCandRace4.GetColumn(i,"CCIT_CNT") );
				if ( ccit4.length > 0 ){ 
					Div0.edRaceCount4.Text = ccit4;
					break;
				}
			}


			var bt4 = "";
			for(var i=0; i<dsOutCandRace4.count; i++){
				bt4 = toString( dsOutCandRace4.GetColumn(i,"BOAT_TIME") );
				if ( bt4.length > 0 ){ 
					Div0.edRaceTime4.text = bt4;
					break;
				}
			}

	 } 

     if (sServiceName=="d06000023-service:searchChulju5")
	 {
			var round5 = "";
			for(var i=0; i<dsOutCandRace5.count; i++){
				round5 = toString( dsOutCandRace5.GetColumn(i,"ROUND") );
				if ( round5.length > 0 ){ 
					Div0.cbRound5.value = round5;
					break;
				}
			}


			var gbn5 = "";
			for(var i=0; i<dsOutCandRace5.count; i++){
				gbn5 = dsOutCandRace5.GetColumn(i,"GBN");
				if ( gbn5.length > 0 ){ 
					Div0.cbRaceGbn5.value = gbn5;
					break;
				}
			}


			var ccit5 = "";
			for(var i=0; i<dsOutCandRace5.count; i++){
				ccit5 = toString( dsOutCandRace5.GetColumn(i,"CCIT_CNT") );
				if ( ccit5.length > 0 ){ 
					Div0.edRaceCount5.Text = ccit5;
					break;
				}
			}


			var bt5 = "";
			for(var i=0; i<dsOutCandRace5.count; i++){
				bt5 = toString( dsOutCandRace5.GetColumn(i,"BOAT_TIME") );
				if ( bt5.length > 0 ){ 
					Div0.edRaceTime5.text = bt5;
					break;
				}
			}

	 } 



    //ø¿∑˘∞° æ¯¿Ã ¡§ªÛ √≥∏Æµ«æ˙¥Ÿ∏È...
	fcPostProcess(sServiceName);
}



