function grdCandList1_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	if ( length( toString(Div0.cbRound1.Value) ) < 1 || length(Div0.cbRaceGbn1.Value) < 1 ) {
		fnc_Message(SNIS_CAN_E001, "N","∂ÛøÓµÂøÕ √‚¡÷±∏∫–¿ª ");
		return;
	}

	if(length(cbPerioNo.Value) <= 0 || length(calRaceDate.Value) < 1 ){	
		//±‚ºˆ « ºˆ ¿˙¿Â
		fnc_Message(SNIS_CAN_E001, "N", "±‚ºˆøÕ ¿œ¿⁄¥¬ ");
		return false;
	}

	var selCandNo = dsCandIdent_1.GetColumn(nRow,"CAND_NO");
	var selCandNM = dsCandIdent_1.GetColumn(nRow,"NM_KOR");
	var nAddRow = dsOutCandRace1.AddRow();

	//alert(selCandNM);



	dsOutCandRace1.SetColumn(nAddRow,"RACER_PERIO_NO",Trim(cbPerioNo.Value));
	dsOutCandRace1.SetColumn(nAddRow,"DT",calRaceDate.value);
	dsOutCandRace1.SetColumn(nAddRow,"ROUND",Div0.cbRound1.Value);
	dsOutCandRace1.SetColumn(nAddRow,"GBN",Div0.cbRaceGbn1.Value);
	dsOutCandRace1.SetColumn(nAddRow,"CAND_NO",selCandNo);
	dsOutCandRace1.SetColumn(nAddRow,"NM_KOR",selCandNM);

}

