function Radio0_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	

	if(strCode == "0001") {
		Div0.cbRound1.InnerDataset="dsRound1";
		Div0.cbRound2.InnerDataset="dsRound1";
		Div0.cbRound3.InnerDataset="dsRound1";
		Div0.cbRound2.InnerDataset="dsRound1";
		Div0.cbRound5.InnerDataset="dsRound1";
	} else {
		Div0.cbRound1.InnerDataset="dsRound2";
		Div0.cbRound2.InnerDataset="dsRound2";
		Div0.cbRound3.InnerDataset="dsRound2";
		Div0.cbRound2.InnerDataset="dsRound2";
		Div0.cbRound5.InnerDataset="dsRound2";
	}
}


function grdRace1_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	//var arg = "Question_Type="+Quote(strQuestion_Type);
	//rtn = Dialog("MEB00000::MEB0A008POP.xml",arg,,,true,-1,-1);

	//rtn = Dialog("form::D06000023POP.xml",,,,true,-1,-1);

	if (nCell==3) {
		var cd = "001";
		var arg = "grpcode="+Quote(cd) + " arg_title=∏≈Õº±≈√";
		rtn = Dialog("form::D06000023_1.xml",arg,,,true,-1,-1);

		if( rtn == null || rtn == '' ) return;	

		dsOutCandRace1.SetColumn(nRow,"MOTOR_NO",rtn[0]); 
	} 

	if (nCell==4) {
		var cd = "002";
		var arg = "grpcode="+Quote(cd) + " arg_title=∫∏∆Æº±≈√";
		rtn = Dialog("form::D06000023_1.xml",arg,,,true,-1,-1);

		if( rtn == null || rtn == '' ) return;	

		dsOutCandRace1.SetColumn(nRow,"BOAT_NO",rtn[0]); 
	}


	if (nCell==21) {
		var selviol = dsOutCandRace1.GetColumn(nRow,"VIOL");
		var cd = "";
		var violcmt = "";

		for (var i=0; i<dsViol.count; i++) {
			cd  = dsViol.GetColumn(i,"CD");
			if (quote(cd) == quote(selviol)){
				violcmt =  dsViol.GetColumn(i,"PROV");
				dsOutCandRace1.SetColumn(nRow,"VIOL_CNTNT",violcmt);
				break; 
			}
		}

	}
}

function dsOutCandRace1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "VIOL") {
		var cd="";
		var cdnm="";

		for (var i=0; i<dsViol.count; i++) {
			cd = dsViol.GetColumn(i,"CD");

			if ( quote(cd) == quote(varNewValue) ) {
				cdnm = dsViol.GetColumn(i,"PROV");
				dsOutCandRace1.SetColumn(nRow,"VIOL_CNTNT",cdnm);
				break;
			}
		}
	}



}

