/*-------------------------------------------------------------------------------------------------+
>>  «‡ªË¡¶ πˆ∆∞ ≈¨∏Ø „¡ √≥∏Æ
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//DataSet¿« Header ¡§∫∏∞° ¿÷¿ª ∂ß  Add √≥∏Æ 	
	if ( obj.GetForm().id == "divBtnRow_1" ) {
		if ( !fnc_DeleteCheck(dsOutCandRace1, "CHK", "1") ) return;
			grdRace1.SetFocus(); //Record ªË¡¶ »ƒ Grid∑Œ Focus
	}

	if ( obj.GetForm().id == "divBtnRow_2" ) {
		if ( !fnc_DeleteCheck(dsOutCandRace2, "CHK", "1") ) return;
			grdRace1.SetFocus(); //Record ªË¡¶ »ƒ Grid∑Œ Focus
	}

	if ( obj.GetForm().id == "divBtnRow_3" ) {
		if ( !fnc_DeleteCheck(dsOutCandRace3, "CHK", "1") ) return;
			grdRace1.SetFocus(); //Record ªË¡¶ »ƒ Grid∑Œ Focus
	}

	if ( obj.GetForm().id == "divBtnRow_4" ) {
		if ( !fnc_DeleteCheck(dsOutCandRace4, "CHK", "1") ) return;
			grdRace1.SetFocus(); //Record ªË¡¶ »ƒ Grid∑Œ Focus
	}

	if ( obj.GetForm().id == "divBtnRow_5" ) {
		if ( !fnc_DeleteCheck(dsOutCandRace5, "CHK", "1") ) return;
			grdRace1.SetFocus(); //Record ªË¡¶ »ƒ Grid∑Œ Focus
	}
}

function btnAutoAssign1_OnClick(obj)
{
	if (dsCandIdent_1.count < 1 ) {
		fnc_Message(SNIS_CAN_E003, "N");
		return;
	}

	if ( length(cbPerioNo.Value) < 1 || length(calRaceDate.Value) < 1 ) {
		fnc_Message(SNIS_CAN_E001, "N", "±‚ºˆøÕ ¿œ¿⁄¥¬ ");
		return;
	}

	if ( length( toString(Div0.cbRound1.Value) ) < 1 || length( toString(Div0.cbRaceGbn1.Value) ) < 1 ) {
		fnc_Message(SNIS_CAN_E001, "N","∂ÛøÓµÂøÕ √‚¡÷±∏∫–¿ª ");
		return;
	}


	var pcnt = dsCandIdent_1.count;
	var assigncnt = floor(pcnt/6);
	var assignchk = 6*assigncnt;

	var autoteamno = 1;
	var automotorno = 0;
	var autoboatno = 0;



	for ( var i=0; i<dsCandIdent_1.count; i++ ) { // for 1
		var selCandNo = dsCandIdent_1.GetColumn(i,"CAND_NO");
		var selCandNM = dsCandIdent_1.GetColumn(i,"NM_KOR");

		if (i >= assignchk) break;
		var nAddRow = dsOutCandRace1.AddRow();

		for (a=0; a<dsMotor.count; a++) {

		}

		if (i >= dsMotor.count) {
			automotorno = i % dsMotor.count ;
		}
		motorno = dsMotor.GetColumn(automotorno,"EQUIP_NO");

		if (i >= dsBoat.count) {
			autoboatno = i % dsBoat.count ;
		}
		boatno = dsBoat.GetColumn(autoboatno,"EQUIP_NO");


		if (autoteamno > 6) autoteamno=1;

		dsOutCandRace1.SetColumn(nAddRow,"RACER_PERIO_NO",Trim(cbPerioNo.Value));
		dsOutCandRace1.SetColumn(nAddRow,"DT",calRaceDate.value);
		dsOutCandRace1.SetColumn(nAddRow,"ROUND",Div0.cbRound1.Value);
		dsOutCandRace1.SetColumn(nAddRow,"GBN",Div0.cbRaceGbn1.Value);
		dsOutCandRace1.SetColumn(nAddRow,"CAND_NO",selCandNo);
		dsOutCandRace1.SetColumn(nAddRow,"NM_KOR",selCandNM);
		dsOutCandRace1.SetColumn(nAddRow,"TEAM_NO",autoteamno);

		dsOutCandRace1.SetColumn(nAddRow,"MOTOR_NO",motorno);
		dsOutCandRace1.SetColumn(nAddRow,"BOAT_NO",boatno);

		autoteamno = autoteamno + 1;

		automotorno = automotorno + 1;

		autoboatno = autoboatno + 1;

	}// for 1


}


function btnAutoAssign2_OnClick(obj)
{
	if (dsCandIdent_2.count < 1 ) {
		fnc_Message(SNIS_CAN_E003, "N");
		return;
	}

	if ( length(cbPerioNo.Value) < 1 || length(calRaceDate.Value) < 1 ) {
		fnc_Message(SNIS_CAN_E001, "N", "±‚ºˆøÕ ¿œ¿⁄¥¬ ");
		return;
	}

	if ( length( toString(Div0.cbRound2.Value) ) < 1 || length( toString(Div0.cbRaceGbn2.Value) ) < 1 ) {
		fnc_Message(SNIS_CAN_E001, "N","∂ÛøÓµÂøÕ √‚¡÷±∏∫–¿ª ");
		return;
	}


	var pcnt = dsCandIdent_2.count;
	var assigncnt = floor(pcnt/6);
	var assignchk = 6*assigncnt;

	var autoteamno = 1;
	var automotorno = 0;
	var autoboatno = 0;



	for ( var i=0; i<dsCandIdent_2.count; i++ ) { // for 1
		var selCandNo = dsCandIdent_2.GetColumn(i,"CAND_NO");
		var selCandNM = dsCandIdent_2.GetColumn(i,"NM_KOR");

		if (i >= assignchk) break;
		var nAddRow = dsOutCandRace2.AddRow();

		for (a=0; a<dsMotor.count; a++) {

		}

		if (i >= dsMotor.count) {
			automotorno = i % dsMotor.count ;
		}
		motorno = dsMotor.GetColumn(automotorno,"EQUIP_NO");

		if (i >= dsBoat.count) {
			autoboatno = i % dsBoat.count ;
		}
		boatno = dsBoat.GetColumn(autoboatno,"EQUIP_NO");


		if (autoteamno > 6) autoteamno=1;

		dsOutCandRace2.SetColumn(nAddRow,"RACER_PERIO_NO",Trim(cbPerioNo.Value));
		dsOutCandRace2.SetColumn(nAddRow,"DT",calRaceDate.value);
		dsOutCandRace2.SetColumn(nAddRow,"ROUND",Div0.cbRound2.Value);
		dsOutCandRace2.SetColumn(nAddRow,"GBN",Div0.cbRaceGbn2.Value);
		dsOutCandRace2.SetColumn(nAddRow,"CAND_NO",selCandNo);
		dsOutCandRace2.SetColumn(nAddRow,"NM_KOR",selCandNM);
		dsOutCandRace2.SetColumn(nAddRow,"TEAM_NO",autoteamno);

		dsOutCandRace2.SetColumn(nAddRow,"MOTOR_NO",motorno);
		dsOutCandRace2.SetColumn(nAddRow,"BOAT_NO",boatno);

		autoteamno = autoteamno + 1;

		automotorno = automotorno + 1;

		autoboatno = autoboatno + 1;

	}// for 1


}

