function fcGridSort(obj,nCell,nX,nY) {
    fnc_GridSort(obj, object(obj.BindDataset), nCell);
}

function edRaceTime1_OnChanged(obj,strText)
{
	for (var i=0; i<dsOutCandRace1.count; i++) {
		dsOutCandRace1.SetColumn(i,"BOAT_TIME",Div0.edRaceTime1.text);
	}
}

