/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if( sServiceName == "d06009028-service:searchRaceTime" ) {
	   	fnc_SetStatus(SNIS_CAN_0003,  dsCandList.GetRowCount());
		if(dsCandListResult.GetRowCount()>0){
			//http.sync = true;
			//setMonthHeader(dsCandListResult.GetRowCount());
			//http.sync = false;
		}//else{
		//	setNMonthHeader();
		//}

	}	

}

function dsPerioNo_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cbPerioNo.Value = dsPerioNo.GetColumn(0,"RACER_PERIO_NO");
}

function dsCandList_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{	
	http.sync = true;
	setMonthHeader(dsCandListResult.GetRowCount());
	http.sync = false;
}


//Grit Format생성 
function setMonthHeader(a){
	var count = a;

  	var gridFormat = '';
	gridFormat += " <contents> ";
	gridFormat += " 	<format id='Default'> ";

	gridFormat += " 		<columns> ";		
	gridFormat += " 			<col fix='left' width='45'/> ";	
	gridFormat += " 			<col fix='left' width='95'/> ";
	gridFormat += " 			<col fix='left' width='90'/> ";
	gridFormat += " 			<col width='55'/> ";	
	gridFormat += " 			<col width='90'/> ";	
	gridFormat += " 			<col width='65'/> ";	
	gridFormat += " 			<col width='60'/> ";
	gridFormat += " 			<col width='60'/> ";	
	gridFormat += " 			<col width='60'/> ";	

	if(count >0){
		for(var j=1; j<=count; j++){
			gridFormat += " 		<col width='50'/> ";		
			gridFormat += " 		<col width='50'/> ";	
		}	
	}
	gridFormat += " 		</columns> ";

	gridFormat += " 		<head> ";	
	gridFormat += " 			<cell bkcolor='user20' col='0' color='user21' display='text' font='굴림,9' rowspan='2' text='순번'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='1' color='user21' display='text' font='굴림,9' rowspan='2' text='학번'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='2' color='user21' display='text' font='굴림,9' rowspan='2' text='성명'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='3' color='user21' display='text' font='굴림,9' rowspan='2' text='총횟수'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='4' color='user21' display='text' font='굴림,9' rowspan='2' text='평균스타트시간'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='5' color='user21' display='text' font='굴림,9' rowspan='2' text='F누적'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='6' color='user21' display='text' font='굴림,9' rowspan='2' text='L누적'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='7' color='user21' display='text' font='굴림,9' rowspan='2' text='승률'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='8' color='user21' display='text' font='굴림,9' rowspan='2' text='연대율'/> ";
	if(count >0){
		var columnIndex = 9;		
		for(var i=1; i<=count; i++){
			gridFormat += " 			<cell bkcolor='user20' col='"+(columnIndex)+"' color='user21' colspan='2' display='text' font='굴림,9' text='"+dsCandListResult.GetColumn(i-1,"COL_DT")+"' /> ";						
			gridFormat += " 			<cell bkcolor='user20' col='"+(columnIndex++)+"' color='user21' display='text' font='굴림,9' row='1' text='타임'/> ";
			gridFormat += " 			<cell bkcolor='user20' col='"+(columnIndex++)+"' color='user21' display='text' font='굴림,9' row='1' text='착순'/> ";
		}
	}	
	gridFormat += " 		</head> ";

	gridFormat += " 		<body> ";		
	gridFormat += " 			<cell align='right' col='0' display='text' expr='currow+1'/> ";
	gridFormat += " 			<cell align='center' col='1' colid='CAND_NO' display='text'/> ";
	gridFormat += " 			<cell align='center' col='2' colid='NM_KOR' display='text'/> ";
	gridFormat += " 			<cell align='right' col='3' colid='TOTAL_DT' display='text'/> ";
	gridFormat += " 			<cell align='right' col='4' colid='RACE_TIME' display='text'/> ";
	gridFormat += " 			<cell align='right' col='5' colid='VIOL_5' display='text'/> ";
	gridFormat += " 			<cell align='right' col='6' colid='VIOL_6' display='text'/> ";
	gridFormat += " 			<cell align='right' col='7' colid='WIN_RATE' display='text'/> ";
	gridFormat += " 			<cell align='right' col='8' colid='TWO_RATE' display='text'/> ";

	if(count >0){	
		var bodyColumnIndex =9;
		for(var i=1; i<=count; i++){
			gridFormat += " 		<cell align='right' col='"+(bodyColumnIndex++)+"' colid='RACETIME_"+i+"' display='text'/> ";
			gridFormat += " 		<cell align='right' col='"+(bodyColumnIndex++)+"' colid='ARRIVORD_"+i+"' display='text'/> ";
		}		
	}	
	gridFormat += " 		</body> ";	
	gridFormat += " 		<summary> ";	
	gridFormat += " 			<cell align='center' bkcolor='user20' celltype='head' col='0' color='user21' colspan='3' display='text' font='굴림,9' text='기수종합'/> ";	
	gridFormat += " 			<cell align='right' bkcolor='user15' celltype='summary' col='3' display='text' expr='ROUND(AVG(&quot;TOTAL_DT&quot;),2)' font='굴림,9'/> ";	
	gridFormat += " 			<cell align='right' bkcolor='user15' celltype='summary' col='4' display='text' expr='ROUND(AVG(&quot;RACE_TIME&quot;),2)' font='굴림,9'/> ";	
	gridFormat += " 			<cell align='right' bkcolor='user15' celltype='summary' col='5' display='text' expr='ROUND(AVG(&quot;VIOL_5&quot;),2)' font='굴림,9'/> ";	
	gridFormat += " 			<cell align='right' bkcolor='user15' celltype='summary' col='6' display='text' expr='ROUND(AVG(&quot;VIOL_6&quot;),2)' font='굴림,9'/> ";		
	gridFormat += " 			<cell align='right' bkcolor='user15' celltype='summary' col='7' display='text' expr='ROUND(AVG(&quot;WIN_RATE&quot;),2)' font='굴림,9'/> ";	
	gridFormat += " 			<cell align='right' bkcolor='user15' celltype='summary' col='8' display='text' expr='ROUND(AVG(&quot;TWO_RATE&quot;),2)' font='굴림,9'/> ";		

	if(count >0){
		var summaryAColumnIndex =9;
		for(var i=1; i<=count; i++){		
			gridFormat += " 		<cell align='right' bkcolor='user15' celltype='summary' col='"+(summaryAColumnIndex++)+"' display='text' expr='ROUND(AVG(&quot;RACETIME_"+i+"&quot;),2)' font='굴림,9'/> ";
			gridFormat += " 		<cell align='right' bkcolor='user15' celltype='summary' col='"+(summaryAColumnIndex++)+"' display='text' expr='ROUND(AVG(&quot;ARRIVORD_"+i+"&quot;),2)' font='굴림,9'/> ";
		}	
	}		
	gridFormat += " 		</summary> ";	
	gridFormat += " 	</format> ";
	gridFormat += " </contents> ";	

	grdCandList.contents = gridFormat;	
}
function dsCandListResult_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsCandList.Clear();
}
