/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    if ( obj.id == "btnCancel" || obj.id == "btnSearch" ) {
		dsCandList.ClearData();	
		dsCandListResult.ClearData();	
	}   

}

/*-------------------------------------------------------------------------------------------------+
>>  콤보  설정
+-------------------------------------------------------------------------------------------------*/

function fcSetCombo() {

    var sServiceName  = "d06000010-service:searchPerioNo";
    var sInDataSet    = "";
    var sOutDataSet   = " dsPerioNo=dsPerioNo ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);        
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    if(!fnc_IsMandatory(cbPerioNo, "기수")){
		return false;
    }else if(!fnc_IsMandatory(cdGdu, "승정구분")){
		return false;
    }	
	return true;
}

function fcSearch() {

	if(!fcSearchCheck()){
		return;
	}
	http.sync = true;
	fcSearchResult();
	fcSearchCanList();
	http.sync = false;
}


function fcSearchCanList(){

	var sServiceName = "d06009030-service:searchResultList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCandList=dsCandList";
    var pRacerName	 = "";

    fcd_AddParam("pRacerPerioNo", cbPerioNo.Value);    
    fcd_AddParam("pGdu", cdGdu.Value);   

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

function fcSearchResult() {

	var sServiceName = "d06009030-service:searchResult";
    var sInDataSet   = "";
    var sOutDataSet  = "dsCandListResult=dsCandListResult";
    var pRacerName	 = "";

    fcd_AddParam("pRacerPerioNo", cbPerioNo.Value);    
    fcd_AddParam("pGdu", cdGdu.Value);   

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


