//Grit Format생성 
function setMonthHeader(a){
	var count = a;

  	var gridFormat = '';
	gridFormat += " <contents> ";
	gridFormat += " 	<format id='Default'> ";

	gridFormat += " 		<columns> ";
	gridFormat += " 			<col fix='left' width='50'/> ";	
	gridFormat += " 			<col fix='left' width='110'/> ";
	gridFormat += " 			<col fix='left' width='95'/> ";
	gridFormat += " 			<col width='110'/> ";	

	if(count >0){
		for(var j=1; j<=count; j++){
			gridFormat += " 		<col width='70'/> ";		
		}	
	}
	gridFormat += " 		</columns> ";

	gridFormat += " 		<head> ";
	gridFormat += " 			<cell bkcolor='user20' col='0' color='user21' display='text' font='굴림,9' text='순번'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='1' color='user21' display='text' font='굴림,9' text='학번'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='2' color='user21' display='text' font='굴림,9' text='성명'/> ";
	gridFormat += " 			<cell bkcolor='user20' col='3' color='user21' display='text' font='굴림,9' text='누적승정시간'/> ";
	if(count >0){
		var columnIndex = 4;		
		for(var i=1; i<=count; i++){
			gridFormat += " 			<cell bkcolor='user20' col='"+(columnIndex++)+"' color='user21' display='text' font='굴림,9' text='"+dsCandListResult.GetColumn(i-1,"COL_DT")+"' /> ";						
		}
	}	
	gridFormat += " 		</head> ";

	gridFormat += " 		<body> ";
	gridFormat += " 			<cell align='right' col='0' display='text' expr='currow+1'/> ";
	gridFormat += " 			<cell align='center' col='1' colid='CAND_NO' display='text'/> ";
	gridFormat += " 			<cell align='center' col='2' colid='NM_KOR' display='text'/> ";
	gridFormat += " 			<cell align='right' col='3' colid='TOTAL_BOAT_TIME' display='text'/> ";

	if(count >0){	
		var bodyColumnIndex =4;
		for(var i=1; i<=count; i++){
			gridFormat += " 		<cell align='right' col='"+(bodyColumnIndex++)+"' colid='DAY_"+i+"' display='text'/> ";
		}		
	}	
	gridFormat += " 		</body> ";	
	gridFormat += " 		<summary> ";	
	gridFormat += " 			<cell align='center' bkcolor='user20' celltype='head' col='0' color='user21' colspan='2' display='text' font='굴림,9' rowspan='3' text='기수종합'/> ";
	gridFormat += " 			<cell align='center' bkcolor='user20' celltype='head' col='2' color='user21' colspan='2' display='text' font='굴림,9' text='평균'/> ";

	if(count >0){
		var summaryAColumnIndex =4;
		for(var i=1; i<=count; i++){
			gridFormat += " 		<cell align='right' bkcolor='user15' celltype='summary' col='"+(summaryAColumnIndex++)+"' display='text' expr='ROUND(AVG(&quot;DAY_"+i+"&quot;),2)' font='굴림,9'/> ";
		}	
	}	
	gridFormat += " 			<cell align='center' bkcolor='user20' celltype='head' col='2' color='user21' colspan='2' display='text' font='굴림,9' row='1' text='최대'/> ";
	if(count >0){
		var summaryBColumnIndex =4;
		for(var i=1; i<=count; i++){
			gridFormat += " 		<cell align='right' bkcolor='user15' celltype='summary' col='"+(summaryBColumnIndex++)+"' display='text' expr='ROUND(MAX(&quot;DAY_"+i+"&quot;),2)' font='굴림,9'  row='1'/> ";								
		}
	}
	gridFormat += " 			<cell align='center' bkcolor='user20' celltype='head' col='2' color='user21' colspan='2' display='text' font='굴림,9' row='2' text='최소'/> ";
	if(count >0){
		var summaryCColumnIndex =4;
		for(var i=1; i<=count; i++){
			gridFormat += " 		<cell align='right' bkcolor='user15' celltype='summary' col='"+(summaryCColumnIndex++)+"' display='text' expr='ROUND(MIN(&quot;DAY_"+i+"&quot;),2)' font='굴림,9'  row='2'/> ";																
		}
	}
	gridFormat += " 		</summary> ";	
	gridFormat += " 	</format> ";
	gridFormat += " </contents> ";		

	grdCandList.contents = gridFormat;	
}

function dsCandList_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{	
	http.sync = true;
	setMonthHeader(dsCandListResult.GetRowCount());
	http.sync = false;
}

