/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsOutCandRace.ClearData();
}

function fcToExcel(obj) {
	 fnc_OpenExcelExport("grdCandRace");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);

	return true;
}

//조회버튼
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    var sServiceName = "d06010010-service:searchCandRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutCandRace=dsOutCandRace ";

    fcd_AddParam("RACER_PERIO_NO", cbPerioNo  .Value);    
    fcd_AddParam("DT"            , calRaceDate.Value);   

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutCandRace ) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutCandRace;
	sMandatoryColID   = "DT|ROUND_CNT|PLC_CD";
	sMandatoryColName = "출주일자|라운드횟수|장소";
	oGrid             = grdCandRace;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

	if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "d06010010-service:saveCandRace";
    var sInDataSet   = "dsOutCandRace=dsOutCandRace:u ";
    var sOutDataSet  = "dsOutCandRace=dsOutCandRace ";

    fcd_AddParam("RACER_PERIO_NO", cbPerioNo  .Value);    
    fcd_AddParam("DT"            , calRaceDate.Value);   

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	dsOutCandRace.AddRow();
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	for(var i=0; i < dsOutCandRace.GetRowCount(); i++){
		if(dsOutCandRace.GetColumn(i, "CHK") == "1") {
			if(dsOutCandRace.GetColumn(i, "CFM_YN") == "Y") {
				fnc_Message("확정된 출주정보는 삭제할 수 없습니다.", "N");
				return false;
			}
		}
	}

    if ( !fnc_DeleteCheck(dsOutCandRace, "CHK", "1") ) return;
}

