/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	switch (sColIdx){
		case "출주일자" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;
		default :
			break;
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
//        fcSearch();
        return;
	}

    // 조회 후
	if( sServiceName == "d06010010-service:searchCandRace" ) {
        dsOutCandRace.AddColumn("CHK");
	}

    // 저장 후
	if( sServiceName == "d06010010-service:saveCandRace" ) {
        dsOutCandRace.AddColumn("CHK");
	}

    //오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

function dsOutCandRace_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( strColumnID == "DT" ) {
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
			fnc_Message(L_COM_ALT_0016, "N", "출주일자가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	    if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
			fnc_Message(L_COM_ALT_0016, "N", "출주일자가");
			obj.row = nRow;
			obj.SetColumn(nRow, strColumnID, varOldValue);
	    }
	}
}

function grdCandRace_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    if ( dsOutCandRace.GetRowType(nRow) != 'normal' ) {
        alert('저장 후 작업하시기 바랍니다.');
        return;
    }

    // Content로 넘겨지는 arguements
    var sArg    = " Arg_RacerPerioNo="  + quote(dsOutCandRace.GetColumn(nRow, "RACER_PERIO_NO"));
        sArg   += " Arg_DayNo="         + quote(dsOutCandRace.GetColumn(nRow, "DAY_NO"        ));
        sArg   += " Arg_Dt="            + quote(dsOutCandRace.GetColumn(nRow, "DT"            ));

    Dialog("form::D06010011.xml", sArg, 250, 250, "");
    fcSearch(divSearch.btnSearch);
}

