/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {

	if( fnc_IsNull(cbPerioNo.Value) ) {
        // 기수 누락
        fnc_Message(SNIS_CAN_0008, "N", "기수");
        return false;
    }

	return true;
}

//  월 일일 결석사항 조회
function fcSearchAbsList() {
    dsOutAbsList.ClearData();
    dsAbsList.ClearData();
    dsEduTime.ClearData();
    dsYearList.ClearData();

	for(var y=6; y<24; y++) {
		tabMain.tab3.grdAbsList.SetSubCellProp("HEAD", y, 0, "TEXT", "");
		tabMain.tab3.grdAbsList.SetSubCellProp("HEAD", y, 1, "TEXT", "");
	}

    var sServiceName  = "d06300017-service:searchEduAbsList";
    var sInDataSet    = "";
    var sOutDataSet   = " dsAbsList=dsAbsList ";
        sOutDataSet  += " dsEduTime=dsEduTime ";
        sOutDataSet  += " dsYearList=dsYearList ";

     	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));	

	    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


// 월 교육현황 조회
function fcSearch() {
	if ( !fcSearchCheck(obj) ) return;

	dsEduMonthList.ClearData();

    var sServiceName  = "d06300017-service:searchEduMonthList";
    var sInDataSet    = "";
    var sOutDataSet   = " dsEduMonthList=dsEduMonthList ";

     	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));	

	    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

