function fcInit(obj) {
    dsDiaryMst.ClearData();
    dsDiaryAcdnt.ClearData();
    dsDiaryDetl.ClearData();
    dsDiaryAbs.ClearData();
}

// 선수 콤보 조회
function fcSearchRacer() {

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";

    sServiceName = "d07000004-service:searchRacer";
    sInDataSet   = "";
    sOutDataSet  = "dsRacer=dsRacer ";

	fcd_AddParam("DT", Trim(calDt.Value));
	fcd_AddParam("TRNG_ASK_SEQ", Trim(stTrngAskSeq.Text));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

