function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    //교육훈련일지   
    sServiceName = "d07000004-service:saveDiaryMst";
    sInDataSet   = "dsDiaryMst=dsDiaryMst "
    	         + "dsDiaryAcdnt=dsDiaryAcdnt:u "
    	         + "dsDiaryDetl=dsDiaryDetl:u "
    	         + "dsDiaryAbs=dsDiaryAbs:u ";
    sOutDataSet  = "dsDiaryMst=dsDiaryMst "
    	         + "dsDiaryAcdnt=dsDiaryAcdnt "
    	         + "dsDiaryDetl=dsDiaryDetl "
    	         + "dsDiaryAbs=dsDiaryAbs ";

	fcd_AddParam("DT", Trim(calDt.Value));
	fcd_AddParam("TRNG_ASK_SEQ", Trim(stTrngAskSeq.Text));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


