/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {
	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	sBUTTONLIST   = 'F' + sInpt + 'FTFT';
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 화면 초기화
	fcInit();

	calEndDt.Value = fcd_GetSysdate("YYYYMMDD");
	calStrDt.Value = AddMonth(calEndDt.Value, -1);

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}

	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	if (IsExistVar("sAprv", true)) {
		if (sAprv == "T") {
			btnSaveEnd.Visible = false;
			btnSaveEndCancel.Visible = false;
			btnTrngEndAprv.Visible = true;
			btnTrngEndAprvCancel.Visible = true;
		} else {
			btnSaveEnd.Visible = true;
			btnSaveEndCancel.Visible = true;
			btnTrngEndAprv.Visible = false;
			btnTrngEndAprvCancel.Visible = false;
		}		
	} else {
			btnSaveEnd.Visible = true;
			btnSaveEndCancel.Visible = true;
			btnTrngEndAprv.Visible = false;
			btnTrngEndAprvCancel.Visible = false;
	}
    dsOutTrngAsk.ClearData();
//    dsOutTrngAskRacer.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "d07000001-service:searchTrngAsk";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );
	fcd_AddParam(Trim("TRNG_ASK_STAT_CD"), null);
	fcd_AddParam(Trim("NM_KOR          "), null);
	fcd_AddParam(Trim("TRNG_ASK_STAT_CD"), null);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


