/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnExpandEdit(objGrd, nRow, nCell, strVal, nPivotIndex){
	var sColIdx     = objGrd.GetCellText("HEAD", nRow, nCell);  //해당 HEAD명 
	switch (sColIdx){
		case "시작일" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;
		case "종료일" :
			fnc_SetPopupDivCalendar(objGrd, nRow, nCell);
			break;
		default :
			break;
	}
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
		// 조회
		fcSearch();
	}

	// 요청번호별 선수 조회 후
	if ( sServiceName == "d07000001-service:searchTrngAsk" ) {
        fnc_SetStatus(SNIS_CAN_0003, dsOutTrngAsk.GetRowCount());
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	if (sServiceName  == "d07000001-service:EndTrngAsk") {
		fcSendMessage(sJobType);
	}
}
function grdTrngAsk_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if ( dsOutTrngAsk.GetRowType(nRow) != 'normal' ) {
        alert('저장 후 작업하시기 바랍니다.');
        return;
    }

    // Content로 넘겨지는 arguements
    var sArg    = " Arg_TrngAskSeq="  + quote(dsOutTrngAsk.GetColumn(nRow, "TRNG_ASK_SEQ"));
        sArg   += " Arg_TrngAskStrDt=" + quote(dsOutTrngAsk.GetColumn(nRow, "TRNG_ASK_STR_DT"));
        sArg   += " Arg_TrngAskEndDt=" + quote(dsOutTrngAsk.GetColumn(nRow, "TRNG_ASK_END_DT"));

    Dialog("form::D07000003.xml", sArg, 250, 250, "");
}

function btnSaveEnd_OnClick(obj)
{
	fcSaveEnd(obj);
}


function fcSaveEnd(obj)
{
	if (dsOutTrngAsk.rowcount() == 0) return;
	if (!dsOutTrngAsk.GetUpdate()) {
		fnc_message(L_COM_ALT_0027,"N");
		return;
	}

	var sTrngStatCd = "";
	switch (obj.id) {
		case "btnSaveEnd":
			sTrngStatCd = "003";	// 결과입력
			sJobType = "002003";
			break;
		case "btnSaveEndCancel":
			sTrngStatCd = "002";	// 훈련중
			sJobType = "003002";
			break;
		case "btnTrngEndAprv":
			sTrngStatCd = "004";	// 훈련완료
			sJobType = "003004";
			break;
		case "btnTrngEndAprvCancel":
			sTrngStatCd = "003";	// 결과입력
			sJobType = "004003";
			break;
		default:
			return;
	}

    var sServiceName  = "d07000001-service:EndTrngAsk";
    var sInDataSet    = "dsOutTrngAsk=dsOutTrngAsk:u ";
    var sOutDataSet   = "dsOutTrngAsk=dsOutTrngAsk";

	fcd_AddParam("JOB_TYPE", 			sJobType);
	fcd_AddParam("TRNG_ASK_STAT_CD_CNG", 	sTrngStatCd);

	fcd_AddParam(Trim("STR_DT          "), calStrDt.Value       );
	fcd_AddParam(Trim("END_DT          "), calEndDt.Value       );
	fcd_AddParam(Trim("NM_KOR          "), null);
	fcd_AddParam("TRNG_ASK_STAT_CD",   null);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

function dsOutTrngAsk_OnRowPosChanged(obj,nOldRow,nRow)
{
	fcSearchRacer(obj);
}

function fcSearchRacer(obj) {
    var sServiceName = "d07000001-service:searchTrngAskRacer";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAskRacer=dsOutTrngAskRacer ";

    fcd_AddParam("TRNG_ASK_SEQ", dsOutTrngAsk.GetColumn(dsOutTrngAsk.currow, "TRNG_ASK_SEQ"));

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// 쪽지 전송
function fcSendMessage(sJobType) 
{
	var sSendUserId   = gdsUser.GetColumn(0, "USER_ID");		
	var sMsgReceiveId = "";
	var sTitle        = "선수훈련 안내";
	var sLinkMnId     = "";
	var sSenderGBN    = "";

	var sSendMesg     = "";
	if (sJobType == "002003") {		// 결재권자
		sSendMesg = "훈련완료 요청건이 있습니다.";
		sMsgReceiveId = fcMesgRcvr("001");

		sLinkMnId     = "D07000005";
		fnc_SendMessage(sSendUserId								// 송신자 사번 
					, sMsgReceiveId 						// 수신자 사번 
					, sTitle	    					    // 제목
					, sLinkMnId 							// 수신자 번호
					, sSendMesg								// 내용 
					, "comSendMsg"
					, "CANBOA"
					);	
	} else if (sJobType == "003004") {	
		sSendMesg = "훈련완료 건이 존재합니다.";
		sMsgReceiveId = fcMesgRcvr("002");
		sLinkMnId     = "E02070030";
		fnc_SendMessage(sSendUserId								// 송신자 사번 
					, sMsgReceiveId 						// 수신자 사번 
					, sTitle	    					    // 제목
					, sLinkMnId 							// 수신자 번호
					, sSendMesg								// 내용 
					, "comSendMsg"
					, "BOA"
					);	


		sSendMesg     = "훈련완료 요청이 승인되었습니다.";
		sMsgReceiveId = fcMesgRcvr("003");
		sLinkMnId     = "D07000005";
		fnc_SendMessage(sSendUserId								// 송신자 사번 
					, sMsgReceiveId 						// 수신자 사번 
					, sTitle	    					    // 제목
					, sLinkMnId 							// 수신자 번호
					, sSendMesg								// 내용 
					, "comSendMsg"
					, "CANBOA"
					);	
	}

}

function fcMesgRcvr(sSenderGBN) {
	var sMsgReceiveId = "";
	for (var iCnt=0;iCnt<dsMesgRecvr.RowCount();iCnt++) {
		if (dsMesgRecvr.GetColumn(iCnt, "CD_NM") == sSenderGBN) {
			if (sMsgReceiveId <> "") sMsgReceiveId += ",";
			sMsgReceiveId += dsMesgRecvr.GetColumn(iCnt, "RMK");	// 수신자 사번
		}
	}
	return sMsgReceiveId;
}
