function fcSearchCheck(obj) {
   if(!fnc_IsMandatory(calEduStart, "ÈÆ·ÃÀÏ½ÃÀÛÀÏÀÚ")){
		return false;
    }else if(!fnc_IsMandatory(calEduEnd, "ÈÆ·ÃÀÏÁ¾·áÀÏÀÚ")){
		return false;
    }	
	return true;
}
function fcSearch(obj) {
	if (fcSearchCheck() == false) return;

	switch (tab0.TabIndex) {
		case 3 :
			fcSearch_TrngRsltPrt(obj);
			break;
		case 4 :		// ¼÷ºñ ¹Ì³³ÀÚ Á¶È¸
			fcSearch_NotPayFeeList(obj);
			break;
		case 5 :		// ¼÷ºñ ¹Ì³³¼¼ºÎ³»¿ª Á¶È¸
			fcSearch_NotPayFeeListDetail(obj);
			break;
		case 6 :		// ÈÆ·Ã±â·Ï ¼¼ºÎ³»¿ª
			fcSearch_TrngRsltDetail(obj);
			break;
		case 7 :		// ¼±¼öº° ÈÆ·Ã±â·ÏÅë°è
			fcSearch_RacerTrngRslt(obj);
			break;
		case 8 :		// ±â¼öº° ÈÆ·Ã±â·ÏÅë°è
			fcSearch_PerioTrngRslt(obj);
			break;
		case 9 :		// »ç°í ¹× À§¹Ý³»¿ª
			fcSearch_AcdntList(obj);
			break;
		case 10 :		// ¼±¼ö¸í´Ü
			fcSearch_RacerList(obj);
			break;
		default :
			fcSearch_TrngStatYear(obj);
			break;
	}	
}

