function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	if( sServiceName == "common-service:searchCode" ) {   	
		fcSearch();		
	} else if( sServiceName == "d07009005-service:searchPerioResult" ) {   	
	   	fnc_SetStatus(SNIS_CAN_0003,  dsPerioResult.GetRowCount());	

	   	var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     			//·¹Æ÷Æ® ÆÄÀÏÀÌ ÀÖ´Â ¼­ºêµð·ºÅä¸®
		var sRptNm    = "d07009006.jrf";          //·¹Æ÷Æ® ÆÄÀÏ¸í
		var sDataSet  = "dsPerioResult=dsPerioResult";    //"UBI Äõ¸®¸í, MIP Dataset¸í"
		var sArg      = "STND_SDAY="+calEduStart.Text;		
			sArg += " STND_EDAY="+ calEduEnd.Text; 	
		var sRptDrct  = 1;                               	//Ãâ·Â¹æÇâ(0 : ¼öÁ÷, 1 : ¼öÆò)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
	}else if( sServiceName == "d07009005-service:searchPartsResult" ) {   	
	   	fnc_SetStatus(SNIS_CAN_0003,  dsPartsList.GetRowCount());		

	   	var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     			//·¹Æ÷Æ® ÆÄÀÏÀÌ ÀÖ´Â ¼­ºêµð·ºÅä¸®
		var sRptNm    = "d07009005.jrf";          //·¹Æ÷Æ® ÆÄÀÏ¸í
		var sDataSet  = "dsPartsList=dsPartsList";    //"UBI Äõ¸®¸í, MIP Dataset¸í"
		var sArg      = "STND_SDAY="+calEduStart.Text;		
			sArg += " STND_EDAY="+ calEduEnd.Text; 	
		var sRptDrct  = 1;                               	//Ãâ·Â¹æÇâ(0 : ¼öÁ÷, 1 : ¼öÆò)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
	}	

}

