/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
   if(!fnc_IsMandatory(calEduStart, "훈련일시작일자")){
		return false;
    }else if(!fnc_IsMandatory(calEduEnd, "훈련일종료일자")){
		return false;
    }	
	return true;
}
function fcSearch(obj) {
	if (fcSearchCheck() == false) return;

	switch (tab0.TabIndex) {
		case 3 :
			fcSearch_TrngRsltPrt(obj);
			break;
		case 4 :		// 숙비 미납자 조회
			fcSearch_NotPayFeeList(obj);
			break;
		case 5 :		// 숙비 미납세부내역 조회
			fcSearch_NotPayFeeListDetail(obj);
			break;
		case 6 :		// 훈련기록 세부내역
			fcSearch_TrngRsltDetail(obj);
			break;
		case 7 :		// 선수별 훈련기록통계
			fcSearch_RacerTrngRslt(obj);
			break;
		case 8 :		// 기수별 훈련기록통계
			fcSearch_PerioTrngRslt(obj);
			break;
		case 9 :		// 사고 및 위반내역
			fcSearch_AcdntList(obj);
			break;
		case 10 :		// 선수명단
			fcSearch_RacerList(obj);
			break;
		default :
			fcSearch_TrngStatYear(obj);
			break;
	}	
}

function fcSearch_TrngRsltPrt(obj) {

	var sServiceName = "d07000001-service:searchTrngAsk";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTrngAsk=dsOutTrngAsk ";

	fcd_AddParam(Trim("STR_DT"), calEduStart.Value       );
	fcd_AddParam(Trim("END_DT"), calEduEnd.Value       );

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

