			break;			
		case 5:
			fnc_OpenExcelExport("tab0.tab6.grdTrngAskDetail");
			break;			
		case 6:
			fnc_OpenExcelExport("tab0.tab7.grdRacerTrngRslt");
			break;			
		case 7:
			fnc_OpenExcelExport("tab0.tab8.grdRacerTrngRslt");
			break;			
		case 8:
			fnc_OpenExcelExport("tab0.tab9.grdRacerTrngRslt");
			break;			
		case 9:
			fnc_OpenExcelExport("tab0.tab10.grdAcdntList");
			break;			
		case 10:
			fnc_OpenExcelExport("tab0.tab11.grdRacerList");
