/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	if( sServiceName == "common-service:searchCode" ) {   	
		fcSearch();		
	} else if( sServiceName == "d07009005-service:searchPerioResult" ) {   	
	   	fnc_SetStatus(SNIS_CAN_0003,  dsPerioResult.GetRowCount());	

	   	var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
		var sRptNm    = "d07009006.jrf";          //레포트 파일명
		var sDataSet  = "dsPerioResult=dsPerioResult";    //"UBI 쿼리명, MIP Dataset명"
		var sArg      = "STND_SDAY="+calEduStart.Text;		
			sArg += " STND_EDAY="+ calEduEnd.Text; 	
		var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
	}else if( sServiceName == "d07009005-service:searchPartsResult" ) {   	
	   	fnc_SetStatus(SNIS_CAN_0003,  dsPartsList.GetRowCount());		

	   	var logImgUrl = GBL_REPORT_URL+"log.jpg";
		var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
		var sRptNm    = "d07009005.jrf";          //레포트 파일명
		var sDataSet  = "dsPartsList=dsPartsList";    //"UBI 쿼리명, MIP Dataset명"
		var sArg      = "STND_SDAY="+calEduStart.Text;		
			sArg += " STND_EDAY="+ calEduEnd.Text; 	
		var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

		fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
	}	

}

