function fcGridSelAll(obj,nCell,nX,nY,nPivotIndex)
{
	var bSelVal = '1';
	var dsObj = "";

	if (tab0.TabIndex < 4) return;

	if (tab0.TabIndex == 4)	dsObj = dsNotPayFeeList;
	else if (tab0.TabIndex == 10)	dsObj = dsRacerList;

	if (dsObj.rowcount() < 1) return;

	if (nCell <> 0) fcGridSort(obj,nCell,nX,nY);
	else {	
		if (dsObj.GetColumn(0, "CHK") == "1") bSelVal = "0";
		for (var icnt=0;icnt<dsObj.Rowcount();icnt++) {
			dsObj.SetColumn(icnt, "CHK", bSelVal);
		}
	}
}


function fcTabOnChanged(obj,nOldIndex,nNewindex)
{
	if (tab0.TabIndex == 4 || tab0.TabIndex == 10) btnSendSms.Visible = true;	//문자발송
	else					btnSendSms.Visible = false;
	if (tab0.TabIndex < 3)  chkCommuteYn.Visible = true;
	else                    chkCommuteYn.Visible = false;

	var bVisible = true;
	if (tab0.tabindex == 10)  bVisible = false;

	stTerm.Visible = bVisible;
	calEduStart.Visible = bVisible;
	Static26.Visible = bVisible;
	calEduEnd.Visible = bVisible;

	stRacerNm.Visible = !bVisible;
	edRacerNm.Visible = !bVisible;	
}

function fcBtnOnClick(obj)
{
	if (tab0.TabIndex <> 4 && tab0.TabIndex <> 10) return;

	var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
	var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
	var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
	var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sReceiveId          = "";
	var sParams				= "";
	var sTitle              = "경정훈련관리";
	var nDataSet = dsNotPayFeeList;

	if (tab0.TabIndex == 4) {
		nDataSet = dsNotPayFeeList;
	} else if (tab0.TabIndex == 10) {
		nDataSet = dsRacerList;
	}

	for(var i = 0; i <nDataSet.GetRowCount();i++){
		if(nDataSet.GetColumn(i,"CHK") == "1"){
			sReceivePhoneNumber += nDataSet.GetColumn(i,"HP_NO")+";";					
			sReceiveName        += nDataSet.GetColumn(i,"RACER_NM")+";";
			sReceiveId         += nDataSet.GetColumn(i,"RACER_NM")+";";			//경주사업관리에서는 선수명 조회불가
		}							
	}			

	sReceivePhoneNumber = substr(sReceivePhoneNumber,0,LENGTH(sReceivePhoneNumber)-1);
	sReceiveName = substr(sReceiveName,0,LENGTH(sReceiveName)-1);
	sReceiveId = substr(sReceiveId,0,LENGTH(sReceiveId)-1);

	sParams = "sSendPhoneNumber="+quote(sSendPhoneNumber);
	sParams += " sSendUserId="+quote(sSendUserId);
	sParams += " sSendUserName="+quote(sSendUserName);
	sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
	sParams += " sReceiveName="+quote(sReceiveName);
	sParams += " sReceiveId="+quote(sReceiveId);
	sParams += " sTitle="+quote(sTitle);
	sParams += " sRecvrUserType="+quote(sPermTempGbn);

	nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");
}
