		dsDetlEduAssign.ClearData();
    }    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck() {

	fcInit();
	return true;
}

//후보생건강 조회
function fcSearch() {
    if ( !fcSearchCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "d09000002-service:searchHealth";
    sInDataSet   = "";
    sOutDataSet  = " dsHealth=dsHealth "; 
	fcd_AddParam("racerPerioNo", Trim(cbPerioNo.Value));
	fcd_AddParam("gbn", Trim(cbCure.Value));  
	fcd_AddParam("dt", Trim(calDate.Value));   
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

//기수콤보
function fcPerioNo() {
	var racerPerioNo = null;
	var sServiceName = "d09000002-service:searchPerioNo";
    var sInDataSet   = "";
    var sOutDataSet  = " dsPerioNo=dsPerioNo "; 
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크

	// 필수항목체크
	var sMandatoryColID   	= null;
	var sMandatoryColName 	= null;
	var oGrid             	= null;
	var oDataSet			= null;

	if(length(sRacerPerioNo) <= 0 || length(sCandNo) <= 0 ){	
		//기수,후보생번호 필수 저장
		fnc_Message(SNIS_CAN_A013, "N");
		return;
	}

	if(tabMain.TabIndex == 0){
		if ( !fnc_DatasetChangeCheck(dsHealth) ) 
		{
			//저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");	
			return;
		}	
		oDataSet 		= dsHealth;
		sMandatoryColID   = "DT|IJRY_DETL|CURE_GBN|CUR_CNTNT";
		sMandatoryColName = "일짜|부상내역|치료구분|치료내용";
		oGrid             = tabMain.tab1.grdHealth;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}		

	}else if(tabMain.TabIndex == 1){
		if(length(CalSearchDt.value) <= 0){	
			//일자 필수 저장
			fnc_Message(SNIS_CAN_A003, "N");
			return;
		}			
	}

	return true;
}


function fcSave(obj) {

   if ( !fcSaveCheck() ) return;

}

