/*-------------------------------------------------------------------------------------------------+
>>  그리드에서 팝업 처리 
+-------------------------------------------------------------------------------------------------*/

function grdHealth_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(dsHealth.GetColumn(nRow, "DIAG_FILE_DISP_NM") != null){
		var sLocalFileName  = fcd_GetSelectFile("Save", dsHealth.GetColumn(nRow, "DIAG_FILE_DISP_NM"));
		var sServerFileName = dsHealth.GetColumn(nRow, "DIAG_FILE_REAL_NM");
		var sFileSize       = 0;

		if ( sLocalFileName == null ) return;

		fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, nRow, "PROG_BAR", dsHealth);
	}
}

