/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutEquipReprHis)  )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutEquipReprHis;
	sMandatoryColID   = "REPR_DT|EQUIP_NO|REPR_TPE_CDS";
	sMandatoryColName = "정비일자|모터번호|정비유형";
	oGrid             = grdEquipReprHis;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	grdEquipReprHis.SetFocus();
	for (var iCnt=0;iCnt<dsOutOrderPartsList.Rowcount();iCnt++) {
		if (toNumber(dsOutOrderPartsList.GetColumn(iCnt, "DELV_CNT")) < 1) {
			grdOrderPartsList.SetFocus();
			dsOutOrderPartsList.row = iCnt;
			fnc_Message("정비부품의 사용수량을 입력해 주세요!", "N");
			return false;
		}
	}

	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;

    var sServiceName = "e06010040-service:saveEquipReprHis";
    var sInDataSet   = "dsOutEquipReprHis=dsOutEquipReprHis:u ";
        sInDataSet  += "dsOutOrderPartsList=dsOutOrderPartsList:u ";
    var sOutDataSet  = "dsOutEquipReprHis=dsOutEquipReprHis ";
        sOutDataSet += "dsOutOrderPartsList=dsOutOrderPartsList ";

    var equipTpeCd = CURR_EQUIP_TPE;
    fcd_AddParam("REPR_STR_DT", mskReprStrDt.Value);
    fcd_AddParam("REPR_END_DT", mskReprEndDt.Value);
    fcd_AddParam("EQUIP_TPE_CD", equipTpeCd);
    fcd_AddParam("EQUIP_NO", mskEquipNo.Value);
    fcd_AddParam("REPR_TPE_CD", cboReprTpeCd.Value);
    fcd_AddParam("REPR_PARTS", edParts.Text);
    fcd_AddParam("USE_YN", cboUseYn.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	nBfSaveRow = dsOutEquipReprHis.row;
	grdEquipReprHis.Redraw = true;	    
	grdOrderPartsList.Redraw = false;	
}

