/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	if (obj.GetForm().id == "divBtnRowDetail") {
		dsOutEquipReprHis.AddRow();  
		dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_DT", 		CURR_DT);
		dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "MBR_CD", 		CURR_USER_MBR_CD);
		dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "STND_YEAR", 	CURR_STNT_YEAR);
		dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "EQUIP_TPE_CD", 	CURR_EQUIP_TPE);
		dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "EQUIP_NO", 		dsOutEquip.GetColumn(dsOutEquip.rowposition, 'EQUIP_NO'));
		dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_NM", 		gdsUser.GetColumn(0,"USER_NM"));		
		dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "DELV_SEQ", 		Time()+Random(0,100));		
	} else if (obj.GetForm().id == "divBtnRowParts") {
		if (dsOutEquipReprHis.rowcount() < 1) {
			fnc_message("먼저 정비이력을 추가하세요!","N");
			return;
		}
		var args  = "pPartsType="+quote(CURR_EQUIP_TPE);
		    args += "pStndYear="+quote(dsOutEquipReprHis.GetColumn(dsOutEquipReprHis.rowposition, "STND_YEAR"));
		var bRtn = Dialog("form::E06010040P.xml", args, 568, 512, "");
		if(bRtn) {
			var nRow = 0;
			for (var i=0;i<G_ds_return.rowcount();i++) {
				//동일한 것이 있는 지 여부 체크 : 있으면 추가하지 않음(부품코드,단가년도)
				var sPartsCd = G_ds_return.GetColumn(i, "PARTS_CD");
				var sPartsYear = G_ds_return.GetColumn(i, "PARTS_YEAR");
				if (dsOutOrderPartsList.SearchRow("PARTS_CD='" + sPartsCd + "' AND PARTS_YEAR='" + sPartsYear + "'") <> -1) continue;

				var delv_seq = dsOutEquipReprHis.GetColumn(dsOutEquipReprHis.row, "DELV_SEQ");
				if (fnc_IsNull(delv_seq)) dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.row, "DELV_SEQ", Time());	// 이전에 부품정비내역이 없는 경우
				nRow = dsOutOrderPartsList.AddRow();  
				dsOutOrderPartsList.SetColumn(nRow, "STND_YEAR", 		dsOutEquipReprHis.GetColumn(dsOutEquipReprHis.rowposition, "STND_YEAR"));
				dsOutOrderPartsList.SetColumn(nRow, "MBR_CD", 			CURR_USER_MBR_CD);
				dsOutOrderPartsList.SetColumn(nRow, "DELV_SEQ", 		dsOutEquipReprHis.GetColumn(dsOutEquipReprHis.rowposition, "DELV_SEQ"));		
				dsOutOrderPartsList.SetColumn(nRow, "PARTS_CD", 		sPartsCd);
				dsOutOrderPartsList.SetColumn(nRow, "PARTS_YEAR", 	    sPartsYear);
				dsOutOrderPartsList.SetColumn(nRow, "NOW_UNIT_CNT", 	G_ds_return.GetColumn(i, "NOW_UNIT_CNT"));
				dsOutOrderPartsList.SetColumn(nRow, "PARTS_ITEM_CD_NM", G_ds_return.GetColumn(i, "PARTS_ITEM_CD_NM"));
				dsOutOrderPartsList.SetColumn(nRow, "MODULE_CODE", 		G_ds_return.GetColumn(i, "MODULE_CODE"));
				dsOutOrderPartsList.SetColumn(nRow, "SPEC", 			G_ds_return.GetColumn(i, "SPEC"));
				dsOutOrderPartsList.SetColumn(nRow, "DELV_CNT", 		1);
				dsOutOrderPartsList.SetColumn(nRow, "ORG_DELV_CNT", 	0);
			}
		}
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if (obj.GetForm().id == "divBtnRowDetail") {
		if ( fnc_DeleteCheck(dsOutEquipReprHis, "CHK", "1") ) {
			fcPartsFilter();
		}
	} else if (obj.GetForm().id == "divBtnRowParts") {
		if ( fnc_DeleteCheck(dsOutOrderPartsList, "CHK", "1") ) {
			genOrderCon();
		}
	}
}

