/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {

    } else if( sServiceName == "e06010040-service:searchEquip" ) {

	} else if( sServiceName == "e06010040-service:searchEquipReprHis" ) {
	    if( dsOutEquipReprHis.GetRowCount() > 0 ) {
			fnc_SetStatus(SNIS_BOA_0003,  dsOutEquipReprHis.GetRowCount());

			if(chkEquipOption.Value =='1') {	
				dsOutEquipReprHis.Filter("EQUIP_NO='"+dsOutEquip.GetColumn(dsOutEquip.Row, "EQUIP_NO")+"'");
			} else {
				dsOutEquipReprHis.Filter("");
			}			
	    }
		fcPartsFilter();
	    grdOrderPartsList.Redraw = true;
	} else if ( sServiceName == "e06010040-service:saveEquipReprHis" ) {
		if(chkEquipOption.Value =='1') {	
			dsOutEquipReprHis.Filter("EQUIP_NO='"+dsOutEquip.GetColumn(dsOutEquip.Row, "EQUIP_NO")+"'");
		} else {
			dsOutEquipReprHis.Filter("");
		}			

		dsOutEquipReprHis.row = nBfSaveRow;
		dsOutEquipReprHis.ClearSelect();
		dsOutEquipReprHis.SelectRow(nBfSaveRow);
		fcPartsFilter();
		grdEquipReprHis.Redraw = true;
	    grdOrderPartsList.Redraw = true;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

