// 장비 조회  
function fcSearchEquip(){
	var sServiceName = "e06010040-service:searchEquip";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutEquip=dsOutEquip";


    var equipTpeCd = CURR_EQUIP_TPE;
    fcd_AddParam("REPR_STR_DT", mskReprStrDt.Value);
    fcd_AddParam("REPR_END_DT", mskReprEndDt.Value);
    fcd_AddParam("EQUIP_TPE_CD", equipTpeCd);
    fcd_AddParam("EQUIP_NO", mskEquipNo.Value);
    fcd_AddParam("MBR_CD", GBL_MBRCD);
    fcd_AddParam("REPR_PARTS", edParts.Text);
    fcd_AddParam("USE_YN", cboUseYn.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


