//정비 이력중 정비유형이 변경된경우 
function dsOutGrdReprTpeCd_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=='CHK' and varOldValue != varNewValue){
		bindFilds(strColumnID=='REPR_TPE_CDS' and varOldValue != varNewValue);
	}
}



function bindFilds(){
	var arrCheckedCdList  = array(); 
	var arrCheckedNmList  = array(); 
	for(i=0; i<dsOutGrdReprTpeCd.GetRowCount(); i++){
		if(dsOutGrdReprTpeCd.getColumn(i, 'CHK')=='1'){
			arrCheckedCdList[arrCheckedCdList.length] = dsOutGrdReprTpeCd.getColumn(i, 'CD');
			arrCheckedNmList[arrCheckedNmList.length] = dsOutGrdReprTpeCd.getColumn(i, 'CD_NM');
		}
	}
	var reprTpeNms = "";
	var reprTpeCds = "";
	for(i=0; i<arrCheckedNmList.length; i++){
		reprTpeCds += arrCheckedCdList[i];
		reprTpeNms += arrCheckedNmList[i];
		if(i < arrCheckedCdList.length-1){
			reprTpeNms += ",";
			reprTpeCds += ",";
		}
	}

	dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_TPE_CDS", reprTpeCds);
	dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "REPR_TPE_NMS", reprTpeNms);
}

function dsOutEquipReprHis_OnRowPosChanged(obj,nOldRow,nRow)
{	
	//grdReprTpe.Enable = false;
	grdReprTpe.Editable = false;
	dsOutGrdReprTpeCd.Reset();
	var reprTpeCds = Split(dsOutEquipReprHis.getColumn(dsOutEquipReprHis.rowposition, "REPR_TPE_CDS"),',');
	for(i=0; i<reprTpeCds.length; i++){
		for(j=0; j<dsOutGrdReprTpeCd.getRowCount(); j++){
			if(dsOutGrdReprTpeCd.GetColumn(j, 'CD') == reprTpeCds[i]){
				dsOutGrdReprTpeCd.SetColumn(j, "CHK", 1);
			}
		}
	}
	if (dsOutGrdReprTpeCd.rowcount()>0) {
		dsOutGrdReprTpeCd.sort("CHK:D,CD:A");	
	}

	//수정 여부 check
	//dsOutEquipReprHis.SetColumn(nOldRow, 'MODIFY_IMG',null);
	if(dsOutEquipReprHis.GetRowType(nOldRow)=='normal'){
		//dsOutEquipReprHis.SetColumn(nOldRow, 'MODIFY_IMG',null);
	}else{
		dsOutEquipReprHis.SetColumn(nOldRow, 'MODIFY_IMG','ds_modify');
	}	

	fcPartsFilter();
}

function fcPartsFilter()
{
	if (dsOutEquipReprHis.rowcount() == 0) return;

	var strFilter = "";
	var nRow = dsOutEquipReprHis.row;
	if (nRow<0) return;

	// 정비부품 필터링
	strFilter = "DELV_SEQ="+ dsOutEquipReprHis.GetColumn(nRow, "DELV_SEQ") + "";
	strFilter += " AND STND_YEAR='"+ dsOutEquipReprHis.GetColumn(nRow, "STND_YEAR") + "'";	

	//신규 정비이력 등록 시
	if(dsOutEquipReprHis.GetColumn(nRow, "DELV_SEQ") == NULL) {
		strFilter = "DELV_SEQ=999999 AND STND_YEAR='9999'";
	}	
	dsOutOrderPartsList.Filter(strFilter);
}

