		grdReprTpe.Editable = true;
	}
}


function dsOutEquipReprHis_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "HOMEPAGE_YN"){
		var homepageYn = varNewValue;
		if(homepageYn == "1"){
			if(fnc_Message(SNIS_BOA_F010, "Y" )){
				dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "HOMEPAGE_DESC", dsOutEquipReprHis.GetColumn(dsOutEquipReprHis.rowposition, "REPR_DESC"));
			}else{
				chkHomepageYn.value=varOldValue;
				return;
			}
		}else{
			if(fnc_Message(SNIS_BOA_F011, "Y" )){
				dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.rowposition, "HOMEPAGE_DESC", "");
			}else{
				chkHomepageYn.value=varOldValue;
				return;
			}
		}

	}
}


