/*
 * 원본 Dataset의 선택행 값을 return Dataset에 카피한다.
 *
 * param dsSrc - 원본 Dataset 객체
 * param rowIdxs - 선택행 index, 여러개일 경우 '^'문자로 구분한다. (0^1^2^3^4 ...) : Nullable
 * param dsSrc - return Dataset : Nullable
 *
 * return void
 */
function fcSetReturnDs(dsSrc, rowIdx, dsReturn) {

    var nRnum;
    var arrRowIdx;

    if(rowIdx==null) {
        rowIdx = dsSrc.GetCurrow();
    }

    if(dsReturn==null) {
        dsReturn = G_ds_return;
    }

    arrRowIdx = split(rowIdx, "^");
    dsReturn.Copy(dsSrc);
	dsReturn.DeleteAll();

	for(var i=0; i<length(arrRowIdx); i++) {
        nRnum = dsReturn.AppendRow();
        dsReturn.CopyRow(nRnum, dsSrc, arrRowIdx[i]);
	}
}




function genOrderCon()
{

	var rowCnt = dsOutOrderPartsList.GetRowCount();
	var orcerPartsCnt = 0;
	var orderCon = "";
	var firstPartsNm = "";

	for(i=0; i<rowCnt; i++){
		orcerPartsCnt += dsOutOrderPartsList.GetColumn(i, 'DELV_CNT');
		if(i==0) firstPartsNm = dsOutOrderPartsList.GetColumn(i, 'PARTS_ITEM_CD_NM');
	}

	if(rowCnt>1){
		orderCon = firstPartsNm + ' ' + '외 ' + (rowCnt-1) +'품목/ ' + orcerPartsCnt +'개';
	}else{
		orderCon = firstPartsNm + ' '  + orcerPartsCnt +'개';
	}
	dsOutEquipReprHis.SetColumn(dsOutEquipReprHis.row, 'MJR_PARTS', orderCon);
}



function dsOutOrderPartsList_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "DELV_CNT" and varOldValue != varNewValue){
		var NowUnitCnt = 0;
		NowUnitCnt = dsOutOrderPartsList.GetColumn(nRow, "NOW_UNIT_CNT");
		if (ToNumber(varNewValue) > ToNumber(NowUnitCnt)) {
			var sMeSageCode = SNIS_BOA_F016;
			if ( fnc_Message(sMeSageCode, "Y", NowUnitCnt) == false) {
				dsOutOrderPartsList.SetColumn(nRow, "DELV_CNT", varOldValue);
				return false;	
			}
		}
		//genOrderCon();
	}
}


function chkEquipOption_OnClick(obj,strValue)
{
	if(chkEquipOption.Value =='1') {	
		dsOutEquipReprHis.Filter("EQUIP_NO='"+dsOutEquip.GetColumn(dsOutEquip.Row, "EQUIP_NO")+"'");
	} else {
		dsOutEquipReprHis.Filter("");
	}
}

function dsOutOrderPartsList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	genOrderCon();
}
