/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	fcd_AddParam("STND_YEAR", cboStndYear.Value);	
    fcd_AddParam('MBR_CD',    GBL_MBRCD);    
    fcd_AddParam("PARTS_ITEM_CD_NM", edItemCdNm.Text);
    fcd_AddParam("PARTS_TPE", cboPartsTpe.Value);			// 
    fcd_AddParam("PARTS_GBN", "P");				// 장비구분
    fcd_AddParam("CHK_NOW", chkNow.value);					// 보유 여부:Y:보유, N:전체


    var sServiceName = "e06020010-service:searchPartsByDelv";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutParts=dsOutParts ";
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


