/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "common-service:searchCode" ) {			 
		dsOutPartsTpe.filter("CD<>"+quote("W"));
		if (IsExistVar("pPartsType")) cboPartsTpe.value = pPartsType;
		if (IsExistVar("pStndYear"))  cboStndYear.value = pStndYear;

		fcSearch();

	} else if ( sServiceName == "e06020050-service:searchDelv" ) {				
	    if(dsOutOrderPartsList.GetRowCount()>0){
			fnc_SetStatus(SNIS_BOA_0003,  dsOutOrderPartsList.GetRowCount());			
	    }else{
			fnc_SetStatus(SNIS_BOA_0003,  0);
	    }
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}


/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

