function dsOutOrderList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	if(strColumnID =="DELV_DT"){	
		var max = 0;
		var min = 0;
		for(var i = 0 ; i <= dsOutOrderList.GetRowCount(); i++) {
			min = toNumber(dsOutOrderList.GetColumn(i, "DELV_DT"));			
			if(max < min){
				max = min;
			}
		}
		calIjryEdt.Value = toString(max);			
	}
}


//출고수량 등록시
function dsOutOrderPartsList_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID =="DELV_CNT"){
		if(varOldValue != varNewValue)genDelvCon();
		var currCnt = parseFloat(dsOutOrderPartsList.GetColumn(nRow, "NOW_UNIT_CNT"));		
		var delvCnt = parseFloat(dsOutOrderPartsList.GetColumn(nRow, "DELV_CNT"));		

		if(currCnt < delvCnt){
			dsOutOrderPartsList.SetColumn(nRow,"DELV_CNT",varOldValue);	
			fnc_Message(SNIS_BOA_F004,'출고');
		}else{			
		}		
	}
}


function edItemCdNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13 && edItemCdNm.Text != "" ) {
		fcSearch();
	}
}


function fcChoice(obj)
{
	var nChkedIdx = dsOutParts.FindRow("CHK", 1);
	if(nChkedIdx==-1) {
        fcd_SetReturnDs(dsOutParts, dsOutParts.GetCurrow());
	} else {
        var sRowIdx;
        for(var i=0; i<dsOutParts.GetRowCount(); i++) {
            if(dsOutParts.GetColumn(i, "CHK")==1) {
                sRowIdx += i + "^";
            }
        }
        sRowIdx = mid(sRowIdx, 0, length(sRowIdx)-1);
        fcd_SetReturnDs(dsOutParts, sRowIdx);
	}
	close(true);
}

function chkNow_OnClick(obj,strValue)
{
	if (chkNow.Value == "Y") {
		chkNow.text = "보유";
	} else {
		chkNow.text = "전체";
	}
}
