	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	// 공지사항 조회
	fcSearch(1, sNOTICECLSCD);

	// Q&A 조회
	fcSearch(1, sQNACLSCD);

	// 화면 초기화
	fcInit();

	// 마지막 접속 정보 조회
    fcSearchLastLogin();

	SetLastLoginInfo();	// 이전 로그아웃 정보 저장	
}


