/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
//		fcd_AddCodeRow(dsOutRacerDgre, "전체", "ALL");
//		fcSearch();
		if (dsDB.rowcount() > 0) {
		//Trace(dsDB.savexml());
			FrameTop.st_db_check.text = dsDB.GetColumn(0, "CD_NM");
		}
	} else if ( sServiceName == "d05000005-service:searchBoardList" ) {
        fcSetPageCombo();
	} else if ( sServiceName == "common_login-service:searchLastLoginDT") {
		if (dsLastLoginDT.rowcount() > 0) {
			var sLastLoginInfo = "";
			sLastLoginInfo  = " 최근 접속일시 : "+ dsLastLoginDT.GetColumn(0, "LOGIN_DT");
			sLastLoginInfo += ", "+ dsLastLoginDT.GetColumn(0, "PC_IP");			
			Global.FrameBottom.stLastLoginDt.Text = sLastLoginInfo;
		}
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);

}


/*-------------------------------------------------------------------------------------------------+
>>  글쓰기버튼 클릭시
+-------------------------------------------------------------------------------------------------*/
function btnNoticeWrite_onClick(obj)
{
    // Content로 넘겨지는 arguements
	var sArg  = "Arg_clsCd=" + quote(sNOTICECLSCD);

    if ( Dialog("form::D05000006.xml", sArg, 250, 250, "") ) fcSearch(noticePageCombo.Value, sNOTICECLSCD);	
}

function Button0_OnClick(obj)
{
    // Content로 넘겨지는 arguements
	var sArg  = "Arg_clsCd=" + quote(sQNACLSCD);

    if ( Dialog("form::D05000006.xml", sArg, 250, 250, "") ) fcSearch(qnaPageCombo.Value, sQNACLSCD);
}


/*-------------------------------------------------------------------------------------------------+
>>  게시글 더블클릭시
+-------------------------------------------------------------------------------------------------*/
function grd_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var seqNo       = ""; 
    var sClsCd      = "";
    if(obj.id == "grdNotice") {
        seqNo   = dsOutNotice.getColumn(nRow, "SEQ_NO");
        sClsCd  = sNOTICECLSCD;        
    } else if(obj.id == "grdQna") {
        seqNo   = dsOutQna.getColumn(nRow, "SEQ_NO");
        sClsCd  = sQNACLSCD;
    }

	if ( fnc_IsNull(seqNo) ) return;

    // Content로 넘겨지는 arguements
	var sArg    = "Arg_seqNo="  + quote(seqNo);
        sArg   += " Arg_clsCd=" + quote(sClsCd);

    if ( Dialog("form::D05000006.xml", sArg, 250, 250, "") ) fcSearch(qnaPageCombo.Value, sClsCd);
}


