function fcSearch(currentPage, clsCd) {
	var sServiceName = "d05000005-service:searchBoardList";
    var sInDataSet   = "";
    var sOutDataSet  = "";

	fcd_AddParam(Trim("DEL_YN") , "N"   );
	fcd_AddParam(Trim("CLS_CD") , clsCd );

	fcd_AddParam(Trim("ROWNUM1"), currentPage  ); 
    fcd_AddParam(Trim("ROWNUM3"), currentPage  ); 

	fcd_AddParam(Trim("ROWNUM2"), sPAGENUM   ); 
	fcd_AddParam(Trim("ROWNUM4"), sPAGENUM   ); 

	var searchKey = "";

    if ( clsCd == "001" ) {
        sOutDataSet  = "dsOutNotice=dsInsertBoard ";
        sOutDataSet += "dsOutNoticeCount=dsListValueCount ";
        switch ( searchNoticeCombo.index ) {
            case 0 : searchKey = "SBJT";        break;
            case 1 : searchKey = "BORD_DESC";   break;
            case 2 : searchKey = "MAKE_MAN";     break;
        }
        fcd_AddParam(searchKey, edSearchNoticeText.text);                
    }

    if ( clsCd == "002" ) {
        sOutDataSet  = "dsOutQna=dsInsertBoard ";
        sOutDataSet += "dsOutQnaCount=dsListValueCount ";
        switch ( searchQnaCombo.index ) {
            case 0 : searchKey = "SBJT";        break;
            case 1 : searchKey = "BORD_DESC";   break;
            case 2 : searchKey = "MAKE_MAN";     break;
        }
        fcd_AddParam(searchKey, edSearchQnaText.text);        
    }	


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcSetPageCombo() {
    var pNoticeComboIndex   = 0;
    var pQnaComboIndex      = 0;

    if(noticePageCombo.index > -1) pNoticeComboIndex = noticePageCombo.index;
    dsOutPageNotice.ClearData();
    var nTotalCountNotice = ceil(toInteger(dsOutNoticeCount.getColumn(0, "TOTCOUNT")) / toInteger(sPageNum));
    if(nTotalCountNotice <= 0) noticePageCombo.visible = true;
    else {
        for ( var i = 0; i < nTotalCountNotice; i++ ) {
            dsOutPageNotice.addRow();
            dsOutPageNotice.setColumn(i, "TOTCOUNT", toString(i + toInteger(1)));
        } // end for

        noticePageCombo.index = pNoticeComboIndex;
    }

    if(qnaPageCombo.index > -1) pQnaComboIndex = qnaPageCombo.index;
    dsOutPageQna.ClearData();
    var nTotalCountQna = ceil(toInteger(dsOutQnaCount.getColumn(0, "TOTCOUNT")) / toInteger(sPageNum));
    if(nTotalCountQna <= 0) qnaPageCombo.visible = true;
    else {       
        for ( var i = 0; i < nTotalCountQna; i++ ) {
            dsOutPageQna.addRow();
            dsOutPageQna.setColumn(i, "TOTCOUNT", toString(i + toInteger(1)));
        } // end for

        qnaPageCombo.index = pQnaComboIndex;
    }
}


function SetLastLoginInfo()
{
	// 마지막 접속 정보를 레지스트리에서 조회하여 최종 종료시간을 저장한다.
	// 최종 종료시간은 1회만 저장이 가능하다(SQL처리:LOGOUT_DT가 NULL 인 경우)
	var sServiceName = "common_login-service:SaveUserTrace";
    var sInDataSet   = "dsUserTrace=dsUserTrace ";
    var sOutDataSet  = "";

    var sLastLogSeq  = GetReg("LastLogSeq");
    var sLastLoginDt = GetReg("LastLoginDt");

    if (sLastLogSeq == "") return;		// 자료가 없으면 종료
    SetReg("LastLogSeq", "");
	SetReg("LastLoginDt", "");

    var arLogSeq  = split(sLastLogSeq, "|");
    var arLoginDt = split(sLastLoginDt, "|");
    var nRow = 0;
    for (var i=1;i<length(arLogSeq);i++) {
		nRow = dsUserTrace.AddRow();
		dsUserTrace.SetColumn(nRow, "LOG_SEQ", arLogSeq[i]);
		dsUserTrace.SetColumn(nRow, "LOGOUT_DT", arLoginDt[i]);
    }
	fcd_AddParam("JOB_TYPE", "E");    

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

// 최조 접속시에 서버에서 받아온 시간으로 PC의 시간을 수정한다.
// 기존 fcd_GetSysdate()함수는 다시 서버로 조회하지 않는다.
function LocalTimeSetting()
{
	var setRet = ext_SetTime(fcd_GetSysdate("YYYYMMDDHH24MISS", "SERVER"));
	if (setRet <> "1") Trace("PC 시간 설정 오류:common>comMain>LocalTimeSetting()");
}



function fcSearchLastLogin(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "common_login-service:searchLastLoginDT";
    var sInDataSet   = "";
    var sOutDataSet  = "dsLastLoginDT=dsLastLoginDT";

    fcd_AddParam("USER_ID", gdsUser.getColumn(0, "USER_ID"));
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
