/*-------------------------------------------------------------------------------------------------+
>>  게시글 더블클릭시
+-------------------------------------------------------------------------------------------------*/
function grd_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

    var seqNo       = "";
    var sClsCd      = "";
    if(obj.id == "grdNotice") {
        seqNo   = dsOutNotice.getColumn(nRow, "SEQ_NO");
        sClsCd  = sNOTICECLSCD;        
    } else if(obj.id == "grdQna") {
        seqNo   = dsOutQna.getColumn(nRow, "SEQ_NO");
        sClsCd  = sQNACLSCD;
    } else if(obj.id == "grdProcessProg") {
        seqNo = dsOutProcess.getColumn(nRow, "SEQ_NO");
    }

	if ( fnc_IsNull(seqNo) ) return;

    // Content로 넘겨지는 arguements
    var sArg    = "Arg_seqNo="  + quote(seqNo);
        sArg   += " Arg_clsCd=" + quote(sClsCd);

	if(obj.id == "grdProcessProg") {
        if ( Dialog("form::D01010061.xml", sArg, 250, 250, "") ) fcGetProcessProg();    
    } else {
        if ( Dialog("form::D01000007.xml", sArg, 250, 250, "") ) fcSearch(qnaPageCombo.Value, sClsCd);
    }
}


