function fcSearch(obj) {
    var sServiceName = "d05000003-service:searchGrpMenu";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuGrpValue=dsMenuGrpValue";

    fcd_AddParam("MN_ID", cbMenuGrp.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 필수항목체크
	oDataSet          = "dsMenuListValue";
	sMandatoryColID   = "MN_ID|MN_NM|UP_MN_ID|URL";
	sMandatoryColName = "메뉴ID|메뉴명|상위메뉴ID|URL";
	oGrid             = "";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
    return true;
}

function fcSaveSet() {
    var urlVal = "";
    var curRowType = "";


    for(var i=0; i<dsMenuListValue.rowcount; i++) {
        urlVal     = trim(dsMenuListValue.GetColumn(i, "URL"));
        curRowType = dsMenuListValue.GetRowType(i);

        if(!fnc_IsNull(urlVal) && (curRowType != "normal")) {
            dsMenuListValue.SetColumn(i,"SCRN_ID",SubStr(urlVal, 6, 9));
        }    
    }
}

function fcSave(obj) {
    if ( !fcSaveCheck(obj) ) return;

    fcSaveSet();

    var sServiceName = "d05000003-service:saveMenu";
    var sInDataSet   = " dsMenuGrpValue=dsMenuGrpValue:u ";
        sInDataSet  += " dsMenuListValue=dsMenuListValue:u ";        
    var sOutDataSet  = " dsMenuListValue=dsMenuListValue ";

//    fnc_DatasetPrint(dsMenuGrpValue);
//    fnc_DatasetPrint(dsMenuListValue);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	if ( tbScore.TabIndex == 0 ) {
		fnc_OpenExcelExport("tbScore.tb0.grdRankScr");         //입력 첫 항목으로 Focus
	}

	if ( tbScore.TabIndex == 1 ) {
		fnc_OpenExcelExport("tbScore.tb1.grdAcdntScr");        //입력 첫 항목으로 Focus
	}
}

