function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "common-service:searchCode" ) {
		dsMenuGrpList.InsertRow(0);
		dsMenuGrpList.SetColumn(0, "MN_ID", "");
		dsMenuGrpList.SetColumn(0, "MN_NM", "전체");
		cbMenuGrp.Index = 0;
        fcSearch();
        return;
	 }

    // 메뉴그룹 조회
    if (   sServiceName == "d05000003-service:searchGrpMenu" ) {

    }

    // 메뉴리스트 조회
    if ( sServiceName == "d05000003-service:searchMenu" ) {

    }

    // 메뉴리스트 저장
    if ( sServiceName == "d05000003-service:saveMenu" ) {
       // fcGetMenuDetail("0");
	//	fcGetMenuGrp();
	//	return;
    }    
}

